[[module CSS]]

/*
EPISODE-SPECIFIC STYLING
*/

@import url('https://fonts.googleapis.com/css2?family=Kanit&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@600&display=swap');

#page-content .anom-bar-container.cyber-amida .text-part .disrupt-class::before {
content: "E" !important;
}

#page-content .chat {
background: #081011;
margin: 1.5rem auto;
padding: 0.5rem 1.5rem;
max-width: 45rem;
word-break: break-word;
border: solid 0.35rem rgba(var(--admo-color-alt), 0.3);
border-radius: 0.8rem;
color: rgba(var(--four-color), 1.75);
font-size: 1rem;
font-family: var(--mono-font);
}

#page-content .chat > .chat {
font-weight: normal;
font-family: var(--body-font);
color: #C4C4C4;
background: #000909;
padding: 2rem;
}

@keyframes rotation {
from {
transform: rotate(0deg);
}
to {
transform: rotate(359deg);
}
}

#page-content .vbox::after {
display: block;
content: "";
margin: 1rem auto;
background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6370408-125-jaf5/admo-6488-vjds.png) center no-repeat;
background-size: contain;
width: 100px;
height: 100px;
margin-right: 1rem;
}

#page-content .vjds {
font-family: var(--mono-font);
max-width: 22rem;
margin-left: auto;
padding: 0.6rem 2rem;
background: linear-gradient(130deg, rgb(0, 33, 40) 0%, rgba(0, 33, 40, 0.4) 80%);
color: rgb(var(--four-color));
font-weight: 600;
border-radius: 8px;
}

#page-content .rbox::after {
display: block;
margin: 1rem auto;
content: "» R・ヴェイス";
text-align: left;
font-family: var(--mono-font);
font-style: bold;
font-size: 1.2em;
color: #ddd;
margin-left: 1rem;
}

#page-content .veiss {
font-family: var(--mono-font);
max-width: 19rem;
padding: 0.6rem 2rem;
background: linear-gradient(130deg, rgb(34, 34, 34) 0%, rgba(34, 34, 34, 0.4) 80%);
border-radius: 8px;
}

#page-content .jbox::after {
display: block;
content: "";
margin: 1rem auto;
background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6370408-126-asx4/admo-6488-jldo.png) center no-repeat;
background-size: contain;
width: 100px;
height: 100px;
margin-left: 1rem;
}

#page-content .jldo {
font-family: 'Space Grotesk', var(--body-font);
font-size: 1.2rem;
max-width: 31rem;
margin-right: auto;
padding: 0.6rem 2rem;
background: linear-gradient(130deg, rgb(0, 17, 34) 0%, rgba(0, 17, 34, 0.4) 80%);
color: #9be;
font-weight: 600;
border-radius: 8px;
}

#page-content .ebox::after {
display: block;
content: "";
margin: 1rem auto;
background: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A6370408-127-vgx5/admo-6488-elng.png) center no-repeat;
background-size: contain;
width: 100px;
height: 100px;
margin-left: 1rem;
}

#page-content .elng {
font-family: 'Kanit', var(--body-font);
font-size: 1.3rem;
max-width: 17rem;
margin-right: auto;
padding: 0.6rem 2rem;
background: linear-gradient(130deg, rgb(68, 0, 0) 0%, rgba(68, 0, 0, 0.4) 80%);
color: #faa;
border-radius: 8px;
}

#page-content .kbox::after {
display: block;
margin: 1rem auto;
content: "» V・ケルヴィン";
font-family: var(--mono-font);
font-weight: 600;
font-size: 1.2em;
color: #429530;
margin-left: 1rem;
}

#page-content .kelvin {
font-family: var(--mono-font);
max-width: 21rem;
padding: 0.6rem 2rem;
background: linear-gradient(130deg, rgb(17, 34, 17) 0%, rgba(17, 34, 17, 0.4) 80%);
border-radius: 8px;
}

#page-content .lotus {
background-color: rgb(var(--admo-color-dim));
background-image: url("http://scp-jp-storage.wdfiles.com/local--files/file%3A6370408-132-2b6t/pixels.gif");
background-attachment: fixed;
background-size: 15%;
background-repeat: round;
background-blend-mode: darken;
border: none;
box-shadow: inset 0rem -2rem 10rem 8rem rgba(var(--admo-color-dark))
}

#page-content div.notation {
background-color: rgba(var(--basalt-secondary-color), 0.6);
}

#page-content .lotus .st {
text-decoration: none;
}

#page-content .lotus p:has(.st) {
text-align: center;
}

@media only screen and (max-width: 768px) {
   #page-content .chat {
      padding: 0.5rem 0px;
      font-size: 0.88rem;
   }

   #page-content .chat > p {
      margin: 0.5rem;
   }

   #page-content :is(.vjds, .veiss, .jldo, .elng, .kelvin) {
      margin-left: unset;
      margin-right: unset;
   }

   #page-content .lotus {
      background-size: 33%;
      box-shadow: inset 0rem -2rem 10rem 2rem rgba(var(--admo-color-dark));
   }
}

