[[include :scp-wiki:theme:basalt darkmode=a|hidetitle=a]]
[[module css]]
body {
    background: url(https://www.flickr.com/photos/12836528@N00/35717935330);
    background-color: var(--accentColor);
    background-attachment: fixed;
    background-size: cover;
    background-blend-mode: luminosity;
}

body:before {
  content: ' ';
  position: fixed;
  display: block;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-image: linear-gradient(165deg, transparent, #0c0c0c 55%, #0c0c0c 55%, #0c0c0c 100%);
  z-index: -1;
}

.table2 {
    --accentColor: #42425a;
}

.bigtext {
    color: var(--accentColor);
}

@media (max-width: 1290px) {
    body {
        background-size: 100% 66%;
    }
}

#extra-div-1 {
background: linear-gradient( 180deg, rgb(10, 10, 10) 0%, var(--accentColor)100%);
box-shadow: 0px 0px 8px 4px rgb(0 0 0);
}

:root {
    --title: "SITE-120";
    --subtitle: "FIGHTING FIRE WITH FIRE";
    --side-subtitle: "FIGHTING FIRE WITH FIRE";
    --accentColor: #42425a;
    --four-color: #42425a;
    --logo: url(http://scpdsandbox.wdfiles.com/local--files/darkartists-dead-fairy/120);
    --side-logo: url(http://scpdsandbox.wdfiles.com/local--files/darkartists-dead-fairy/120);
}

.logo {
filter: invert(1);
}

#side-bar::before {
    border-color: #1a1a1a;
    filter: invert(1);
    color: #42425a;
}

.list-pages-item > hr,
#page-content > hr {
background: transparent;
border-top: double 3px var(--accentColor);
filter: brightness(170%);
}

.custom2 .wiki-content-table {
    width: 100%;
}

#page-content tr th {
    text-align: center;
}

.custom2 tr td {
    padding: 10px !important;
}

.bigtext {
color: rgb(196,2,51);
}

.fnnum::after {
    color: #a82d4c !important;
}

.fnnum:hover::after {
    color: white !important;
}

.fncon {
    background: rgb(25, 25, 25) !important;
    color: #EDEDED;
    border: 0.15rem solid rgb(20, 20, 20);
    border-radius: 2px;
    font-size: 90%;
}

::selection {
    background-color: rgba(227, 106, 0, 0.7);
    color: white;
}

::-webkit-scrollbar-thumb {
    background: #42425a;
}

::-webkit-scrollbar-thumb:hover {
    background:  #42425a;
}

.jotting hr, .darkdocument hr, .smallmodal hr, div.blockquote hr, .blockquote hr, blockquote hr {
border-top: solid 2px grey;
}

.modal hr, .doublehr hr, .notation hr {
background: transparent;
border-top: double 4px gray;
}

::selection {
    background-color: rgb(90,54,83);
    color: white;
}

.old {
text-decoration: none;
    border-bottom: none;
    color: rgb(80,80,94);
}

.fae {
    text-decoration:none;
    border-bottom: none;
    color: #a82d4c;
    font-weight: bold;
    font-family: 'Fira Code', monospace;
}

#page-content .table7 tr th {
    background-color: rgb(66,66,90);
    border: 1px solid rgb(66,66,90);
}

#page-content .table7 .st {
    color: rgb(66,66,90);
}

#page-content .table8 tr th {
    background-color: #a82d4c;
    border: 1px solid #a82d4c;
}

#page-content .table8 .st {
    color: #a82d4c;
}

#page-content .table9 tr th {
    background-color: #3bb6c0;
    border: 1px solid #3bb6c0;
    color: black !important;
}

#page-content .table9 .st {
    color: #3bb6c0;
}

.aiad_memo {
    background: url(http://scpdsandbox.wdfiles.com/local--files/darkartists-dead-fairy/AIAD-DARKLOGO) #141414;
    background-size: contain;
    background-position: top center;
    background-repeat: no-repeat;
    font-family: 'Work Sans', sans-serif;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    width: 90%;
    min-height: 4rem;
    max-height: 100rem;
    margin-left: auto;
    margin-right: auto;
    text-align: center;
    padding: 10px 20px 20px 20px;
    word-break: break-word;
    border: 5px solid #3bb5bf;
    color: #3bb5bf;
}

.aiad_memo>p:nth-child(1)::before {
    content: "NOTICE FROM THE ARTIFICIAL INTELIGENCE APPLICATIONS DIVISION\00000a";
    font-family: 'Work Sans', sans-serif;
    font-weight: 900;
    font-size: 130%;
    text-align: center;
    margin-bottom: 1rem;
    padding-bottom: 0.3rem;
    border-bottom: dotted 0.13rem darkslategrey;
    max-width: 49rem;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    flex-direction: column;
    align-content: stretch;
    align-items: center;
    color: #3bb5bf;
}

.parent {
    text-transform: uppercase;
    color: grey;
    font-weight: 600;
    font-family: 'Work Sans', sans-serif;
    margin-top: -1.5rem;
    font-size: 80%;
}

#u-blink-bar p:last-child::after {
    content: "|";
    animation: blink 1.9s infinite;
    position: relative;
    left: 1px;
}

@keyframes blink{
    to {
        opacity: .0;
    }
}
[[/module]]

