.logo-line-wrap {
    position: relative;
    --lbc: #292929;
    width: calc(100% - 20px);
    margin: 1em auto;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 85px;
    overflow: hidden;
}


.logo-main {
    width: var(--cc);
    height: var(--cc);
    background-image: url('http://incbox.wikidot.com/local--files/svg-base/origin-1.svg');
    background-size: contain;
    border-radius: 50%;
    z-index: 1;
    --cc: 81px;
    opacity: .83;
}

.logo-center {
    position: absolute;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #fff;
    border-radius: 50%;
    width: 80px;
    height: 80px;
}

#html-body .logo-line-wrap div {
    max-width: none;
}

.logo-circle {
    position: absolute;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    z-index: 2;
    animation: rotate 3s 0s infinite both linear;
    border: 2px solid var(--lbc);
}
@keyframes rotate {
    from {transform:rotate(0deg)}
     to {transform:rotate(360deg)}
}

.logo-circle.c-2 {
    clip-path: polygon(0 66%, 50% 50%, 50% 0, 85% 0%, 50% 50%, 100% 15%, 100% 48%, 8% 100%, 85% 100%, 0 0, 0 100%, 0% 85%);
    width: calc(100% - 4px);
    height: calc(100% - 4px);
}

.logo-circle.c-1 {
    animation: none;
}

.logo-circle.c-3 {
    width: calc(100% - 8px);
    height: calc(100% - 8px);
    clip-path: polygon(0 59%, 50% 50%, 46% 0, 85% 0%, 50% 50%, 100% 40%, 100% 85%, 50% 50%, 57% 100%, 18% 100%, 50% 50%, 0 100%);
    animation-direction: reverse;
    opacity: .3;
}

.logo-circle.c-4 {
    clip-path: polygon(0 30%, 50% 50%, 0 0, 85% 0%, 50% 50%, 100% 22%, 100% 85%, 50% 50%, 100% 100%, 81% 100%, 50% 50%, 0 100%);
    width: calc(100% - 12px);
    height: calc(100% - 12px);
    animation-duration: 2.5s;
}

.line-c {
    width: 100%;
    height: 5px;
    background: var(--lbc);
    position: absolute;
}



.line-c:before {
    content: '';
    position: absolute;
    background: #fff;
    top: 2px;
    bottom: 2px;
    left: 2px;
    right: 2px;
    }

.line-t:before {
    content: '';
    position: absolute;
    height: 2px;
    background: var(--lbc);
    width: 20px;
    animation: line-l 1.7s 0s infinite both linear;
    transform: translateY(-50%);
}

@keyframes line-l {
    from {left: 50%}
    to   {left: -20px}
}

@keyframes line-r {
    from {right: 50%}
    to   {right: -20px}
}

.line-b:before {
    content: '';
    position: absolute;
    height: 2px;
    background: var(--lbc);
    width: 20px;
    top: 50%;
    animation: line-l 1.4s 0s infinite both linear;
    transform: translateY(-50%);
}

.line-t:after {
    content: '';
    position: absolute;
    height: 2px;
    background: var(--lbc);
    width: 20px;
    animation: line-r 1.9s 0s infinite both linear;
    transform: translateY(-50%);
}

.line-b:after {
    content: '';
    position: absolute;
    height: 2px;
    background: var(--lbc);
    width: 20px;
    top: 50%;
    animation: line-r 1.5s 0s infinite both linear;
    transform: translateY(-50%);
}

