    text-align: center;
}
div:is(.jotting, .transcript) {
border: double 0.15rem #000;
}
#page-content :is(blockquote, .blockquote) {
    border-color: #000;
    font-weight: normal;
    background-color: #060b11;
    color: #cdd9d6;
}

.image-block, .scp-image-block {

border-bottom: none !important;
}


#page-content div:is(.jotting, .transcript) {

max-width: 44rem;
}


