[[module CSS]]
/* ==== COMMON ==== */
 :root {
	--off-white: 255, 255, 255, .9;

	/* == BETTERFOOTNOTES PATCH == */
	--posX: calc(50% - 358px - 13rem);
	--fnLinger: 1.5s;
	--fnColor: rgb(var(--swatch-primary-darkest, 144, 25, 1));
}

 *, a, span {
	word-break: normal;
}

/* ==== ACS PATCH ==== */
 .anom-bar-container.clear-6 .top-center-box > div {
	background-blend-mode: luminosity;
	background-color: var(--accentColor);
}

 #main-content hr,
 .anom-bar > .bottom-box::before {
	background-attachment: fixed;
	background-blend-mode: overlay;
	background-color: rgb(var(--swatch-primary, 133, 0, 5)) !important;
	background-image: url(https://scp-wiki.wdfiles.com/local--files/component%3Aanomaly-class-bar/galactic.jpg);
	background-size: contain;
}

/* == BETTERFOOTNOTES PATCH == */
 .fnnum {
	font-size: .9rem;
}

/* ==== BLACKLIGHT BOX AND MODAL ==== */
 .addenda {
	width: fit-content;
	margin: 0 auto;
	padding: 0 1rem;

	background-color: rgb(var(--swatch-primary-darkest, 144, 25, 1));
}

  .addenda p {
	--wght: 800;
	color: rgb(var(--off-white, 255, 255, 255, .9));

	font-family: var(--title-font);
	font-size: 1.55rem;

}

 .modal {
	width: auto;
	margin: .5rem;
	padding: 15px;

	border: .25rem solid rgb(var(--swatch-primary-darkest,  144, 25, 1));
}

 .modal.light {
	background: rgb(var(--off-white, 255, 255, 255, .9));
}

 .modal.light.council {
	width: 84%;
	margin: auto;

	background: rgb(var(--off-white, 255, 255, 255, .9)) url(http://scp-wiki.wdfiles.com/local--files/the-great-hippo/scp_trans.png) center no-repeat;
}

#page-content .modal.light.council table.wiki-content-table {
	background-color: rgba(244, 244, 244, .25);
}

 #page-content table.wiki-content-table * {
	text-align: center;
}

 .modal.dark {
	background: rgb(var(--swatch-primary-darkest, 144, 25, 1));
}

 .modal.dark .collapsible-block-folded,
 .modal.dark .collapsible-block-unfolded-link {
	--link-color: var(--off-white, 255, 255, 255, .9);
	--hover-link-color: 252, 207, 207, .85;
	--wght: 700;
	padding: .5rem;

	font-size: 1.35rem;
	overflow-wrap: anywhere;
	text-align: center;
	white-space: normal;
}

  @media (max-width: 767px) {
	 .modal.dark .collapsible-block-folded,
	 .modal.dark .collapsible-block-unfolded-link {
		padding: .5rem 0;
		font-size: 1rem;
	}
}

 .modal.dark .blockquote {
	color: hsla(0, 0%, 100%, .8);
}

 .modal.dark .fncon {
	color: black;
}

/* ==== GENERAL FORMATTING ==== */
 h4,
 .addenda p,
 .blbf-main > div::before,
 .modal.dark .collapsible-block-folded {
	font-variant: small-caps;
}

 h4 {
	text-align: center;
}

 #main-content hr {
	width: 100%;
	height: .5rem;

	border: 0;
}
[[/module]]

