[[module CSS]]
/* HEADER */
 :root{
   --header-title: "SERAPIS";
   --header-subtitle: "WARNING - PROCEDURE DRAFTING IN PROGRESS";
}

 div#extra-div-1{
     background-image: url(https://i.imgur.com/Q2TCZnd.png);
     filter: opacity(.25);
}

 @media (max-width: 767px){
     #header h1{
          position: relative;
          top: -.5rem;
    }

     #header h2{
          margin-top: -.5rem;
    }
}

 .meta-title{
     color: hsl(0, 0%, 0%);
     font-family: "Montserrat", sans-serif;
     text-align: center;
     letter-spacing: 1px;
}

 #breadcrumbs{
     display: none;
}

 .pseudocrumbs{
     margin: -2em 0 1em;
     text-align: center;
}

/* FORMATTING */
 body{
     color: hsl(0, 0%, 0%);
     text-underline-offset: 2px;
}

h1,
 h2,
  h3,
   h4,
    h5{
     text-align: center;
     word-break: unset;
}

 hr{
     height: 0;
     margin: 1em 0;

     background-color: transparent;
     border-top: 1px solid hsl(0, 0%, 0%);
}

 .collapsible-block{
     margin: 1em 0;
}

 .scp-image-block.block-right{
     margin: 0 0 1em 2em;
}

/* CUSTOM BLOCKS */
 .report-box{ /* ---- Report Block ---- */
     margin: 1em 0;
     padding: 0 1em;
     background-color: hsl(0, 0%, 100%);
     border: medium solid hsl(0, 0%, 0%);
}

 .report-box hr{
     border-color: hsl(0, 0%, 0%);
}

 .seal{
     height: 150px;
}

@media (max-width: 540px){
     .seal{
          height: 90px;
    }
}

 .footing::before{ /* ---- Page Footing ---- */
     content: " ";
     position: absolute;
     bottom: 1px;
     left: 0;
     right: 0;
     border-bottom: 2px solid hsl(0, 0%, 0%);
}

 .footing{
     position: relative;
     bottom: -2px;
     margin-bottom: 2px;
     border-bottom: 1px solid hsl(0, 0%, 0%);
}
