[[module CSS]]
 @import url(https://fonts.googleapis.com/css2?family=Short+Stack&display=swap); /*SALLY */
 @import url(https://fonts.googleapis.com/css2?family=Chilanka&display=swap); /*BOBBI */
 @import url(https://fonts.googleapis.com/css2?family=Schoolbell&display=swap); /*PEARL*/
 @import url(https://fonts.googleapis.com/css2?family=Klee+One&display=swap); /* MAGGIE */

/* HEADER */
 :root{
   --header-title: "SERAPIS";
   --header-subtitle: "WARNING - PROCEDURE DRAFTING IN PROGRESS";
}

 div#extra-div-1{
     background-image: url(https://i.imgur.com/Q2TCZnd.png);
     filter: opacity(.25);
}

 @media (max-width: 767px){
     #header h1{
          position: relative;
          top: -.5rem;
    }

     #header h2{
          margin-top: -.5rem;
    }
}

 .meta-title{
     color: hsl(0, 0%, 0%);
     font-family: "Montserrat", sans-serif;
     text-align: center;
     letter-spacing: 1px;
}

 #breadcrumbs{
     display: none;
}

 .pseudocrumbs{
     margin: -2em 0 1em;
     text-align: center;
}

/* FORMATTING */
 body{
     color: hsl(0, 0%, 0%);
     text-underline-offset: 2px;
}

h1,
 h2,
  h3,
   h4,
    h5{
     text-align: center;
     word-break: unset;
}

 hr{
     height: 0;
     margin: 1em 0;

     background-color: transparent;
     border-top: 1px solid hsl(0, 0%, 0%);
}

 .collapsible-block{
     margin: 1em 0;
}

 .scp-image-block.block-right{
     margin: 0 0 1em 2em;
}

/* CUSTOM BLOCKS */
 div.evidence{
     margin: 1em 3em;
     padding: 0 1em;

     background-color: hsl(0, 0%, 96%);
     border: 3px double hsl(0, 0%, 60%);
}

 @media (max-width: 479px){
     div.evidence{ margin: 1em 0; }
}

 @media (min-width: 480px) and (max-width: 580px){
     div.evidence{ margin: .5em; }
}

 .page{ /* ---- Page (Adapted From 'RAISA-007710') ---- */
     margin: 1em 0;
     padding: 11px 20px 20px;

     background-image:
        linear-gradient(
          to top,
          hsl(0, 0%, 84%) 0%,
          hsl(0, 0%, 93%) 8%);
     background-position: 0 8px;
     background-size: 100% 1.3rem;
     border: 1px solid gray;

     overflow: hidden;
}

 .journal{ /* ---- Journal (Adapted From 'SCP-4003') ---- */
     margin: 1em 0;
     padding: .9em;

     background-image:
       linear-gradient(
          to top,
          hsl(201, 33%, 84%) 0%,
          hsl(60, 29%, 89%) 8%);
     background-position: 0 8px;
     background-size: 100% 1.3rem;
     border: 1px solid hsl(0, 0%, 80%);
     border-radius: .9em;
}

 .page p,
 .journal p{
     margin: 0;

     font-size: 1.3rem;
     line-height: 1.3rem;
}

/* SALLY */
 .page.sally,
 .journal.sally{
     font-family: "Short Stack", cursive;
}

 .page.sally p{
     position: relative;
     top: .05em;
}

/* BOBBI */
 .page.bobbi,
 .journal.bobbi{
     font-family: "Chilanka", cursive;
}

 .page.bobbi p,
 .journal.bobbi p{
     position: relative;
     top: .15em;
}

/* MAGGIE */
 .page.maggie,
 .journal.maggie{
     font-family: "Klee One", cursive;
}

 .page.maggie p,
 .journal.maggie p{
     position: relative;
     top: -.1em;
}

/* PEARL */
 .page.pearl,
 .journal.pearl{
     font-family: "Schoolbell", cursive;
}

 .page.pearl p,
 .journal.pearl p{
     position: relative;
     top. -.05em;

     font-size: 1.4rem;
}

 .footing::before{ /* ---- Page Footing ---- */
     content: " ";
     position: absolute;
     bottom: 1px;
     left: 0;
     right: 0;
     border-bottom: 2px solid hsl(0, 0%, 0%);
}

 .footing{
     position: relative;
     bottom: -2px;
     margin-bottom: 2px;
     border-bottom: 1px solid hsl(0, 0%, 0%);
}
[[/module]]

