[[module CSS]]
/* HEADER */
 :root{
   --header-title: "SERAPIS";
   --header-subtitle: "WARNING - PROCEDURE DRAFTING IN PROGRESS";
}

 div#extra-div-1{
     background-image: url(https://i.imgur.com/Q2TCZnd.png);
     filter: opacity(.25);
}

 @media (max-width: 767px){
     #header h1{
          position: relative;
          top: -.5rem;
    }
     #header h2{
          margin-top: -.5rem;
    }
}

 .meta-title{
     color: hsl(0, 0%, 0%);

     font-family: "Montserrat", sans-serif;
     text-align: center;
     letter-spacing: 1px;
}

 #breadcrumbs{
     display: none;
}

 .pseudocrumbs{
     margin: -2em 0 1em;

     text-align: center;
}

/* FORMATTING */
 body{
     color: hsl(0, 0%, 0%);

     text-underline-offset: 30%;
}

 h1, h2, h3, h4, h5{
     text-align: center;
     word-break: unset;
}

 hr{
     height: 0;
     margin: 1em 0;

     background-color: transparent;
     border-top: 1px solid hsl(0, 0%, 0%);
}

 .terminal-span{
     font: bold 110%/1.5 var(--mono-font);
     letter-spacing: 0;
}

/* CUSTOM BLOCKS */
 div.evidence{
     margin: 1em 3em;
     padding: 0 1em;

     background-color: hsl(0, 0%, 96%);
     border: 3px double hsl(0, 0%, 60%);
}

 @media (max-width: 479px){
     div.evidence{ margin: 1em 0; }
}

 @media (min-width: 480px) and (max-width: 580px){
     div.evidence{ margin: .5em; }
}

 table div.blockquote{
     margin: 1em 0;
}

 .notes{
     padding: .001em 1em;

     background: hsla(53, 73%, 47%, .15);
     border-top: 10px solid hsl(53, 93%, 37%);
}

 .imagediv tr th{
     margin-bottom: 0;

     font-size: 85%;
     text-align: center;
}

  @media (max-width: 767px){
     .imagediv.wider{
          width: 250px;
    }
}

 .footing::before{ /* ---- Page Footing ---- */
     position: absolute;
     right: 0;
     bottom: 1px;
     left: 0;
     content: " ";

     border-bottom: 2px solid hsl(0, 0%, 0%);
}

 .footing{
     position: relative;
     bottom: -2px;
     margin-bottom: 2px;

     border-bottom: 1px solid hsl(0, 0%, 0%);
}
[[/module]]

