  display: flex;
  width: 100%;
  align-items: center;
  margin-top:-25px;
  margin-bottom:-20px;
}

.flex-child-edge {
  flex-grow: 2;
  height: 1px;
  background-color: black;
  border: 3px solid black; 
  transform: skew(-30deg);
}

.flex-child-text {
  flex-basis: auto;
  flex-grow: 0;
  margin: 0px 5px 0px 5px;
  text-align: center;
  font-size: 13px;
  color: black;
  letter-spacing: 3px;
  font-style: italic;
  text-shadow: none;
}

/* RAISA */

