[[module CSS]]
/* anendlessusername's invaluable correction of the ACS octagon! */
#page-content .quadrants > div {
    top: 2.25%;
    left: 18.5%;
}
[[/module]]

[[module CSS]]
:root {
 --timeDelay: 0.5s;
 --problem: 255, 109, 0
}
a.collapsible-block-link:not(.licensebox a.collapsible-block-link) {
    font-weight: 600;
    color: white;
    padding-top: 4px;
    padding-bottom: 4px;
    padding-left: 7px;
    padding-right: 9px;
    background: rgb(20, 20, 20);
    border-radius: 6px;
    margin-top: 10px;
    margin-bottom: 12px;
}
@keyframes spin {
   100% { transform: rotate(360deg); }
}
div.ettra img {
  z-index: 0;
  animation: spin 20s cubic-bezier(1,1,1,1) infinite;
  filter: drop-shadow(0 0 0.1rem black);
  margin-top: 18px;
}
.ettra_memo {
    background: #F2E0C2;
    background-size: contain;
    background-position: top center;
    background-repeat: no-repeat;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    width: 90%;
    margin-left: auto;
    margin-right: auto;
    margin-bottom: 24px;
    padding: 10px 20px 20px 20px;
    word-break: break-word;
}
.logo_section{
    float:left;
    width: 150px;
    padding: 0 2%;
}
.title_section{
    float:left;
    width: calc(90% - 150px);
}
.text_section{
    float:left;
    width: 95%;
    padding: 0 2%;
}
@media (max-width: 1000px) {
    .logo_section{
        float:left;
        width: 95%;
        padding: 0 2%;
    }
    .title_section{
        float:left;
        width: 95%;
        padding: 0 2%;
    }
}
.ad_memo {
    background: url(https://scp-wiki.wdfiles.com/local--files/scp-7000/ADtransparent.png) #F4DFDF;
    background-size: 400px;
    background-position: center;
    background-repeat: no-repeat;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    width: 90%;
    min-height: 4rem;
    margin-left: auto;
    margin-right: auto;
    padding: 10px 20px 20px 20px;
    word-break: break-word;
}

.ad_memo>p:nth-child(1)::before {
    content: "ANALYTICS DEPARTMENT REPORT\00000a";
    font-family: 'Work Sans', sans-serif;
    font-weight: 900;
    font-size: 130%;
    text-align: center;
    margin-bottom: 1rem;
    padding-bottom: 0.3rem;
    border-bottom: dotted 0.13rem darkslategrey;
    max-width: 49rem;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    flex-direction: column;
    align-content: stretch;
    align-items: center;
}
.overwatch_memo {
    background: #EBEBEB;
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
    box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
    width: 90%;
    margin-left: auto;
    margin-right: auto;
    padding: 10px 30px 20px 20px;
    word-break: break-word;
} 
.overwatch_memo>p:nth-child(1)::before {
    content: "OVERWATCH COMMAND: O5 COUNCIL MINUTES\00000a";
    font-weight: 900;
    font-size: 130%;
    text-align: center;
    margin-bottom: 1rem;
    padding-bottom: 0.55rem;
    border-bottom: dotted 0.13rem darkslategrey;
    max-width: 49rem;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    flex-direction: column;
    align-content: stretch;
    align-items: center;
[[/module]]

