[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Germania+One&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Gentium+Basic:ital,wght@0,400;0,700;1,400;1,700&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Macondo&display=swap');

:root {

--accentColor: rgb(232, 218, 60);
--accentColorLite: rgb(242, 220, 95);
--hue: 51deg;

--lgurl: url(https://scp-wiki.wikidot.com/local--files/scp-7006/triadcouncil.svg);

--header-font: 'Germania One', sans-serif;
--body-font: 'verdana', sans-serif;
--textColor: #fdfdfd;
--textColorAlt: hsl(44.3, 100%, 95.5%);

--bgColor: rgb(3, 51, 8);
--fgColor: rgb(8, 97, 22);

--header-title: "Initiative";
--header-subtitle: "TO SERVE IN ABSOLUTION";

}

html {
scroll-behavior: smooth;
}

