background-attachment: fixed;
background-size: cover;
font-size: 0.81rem;
}

#container {
background-image: linear-gradient( var(--fgColor) 0 100px, rgba(0, 0, 0, 0) 200px 100%);
}

#header h1 a::before {
letter-spacing: 1px !important;
font-size: 1.3em;
line-height: 12px;
}

@keyframes shine {
  from { -webkit-mask-position: 150%; }
  to { -webkit-mask-position: -50%; }
}

#header h2::before {
font-size: 1.5em;
letter-spacing: 10px !important;
margin-top: 6px;
}

#header h2 {
  margin-top: 0.8em;
  display: none;
}

#extra-div-1 {
filter: opacity(.33) drop-shadow(0px 10px 3px #000);
}

#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus, #search-top-box-form input[type="submit"], #search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
font-size: unset;
}

#lock-info {
color: unset;
}

::selection {
color: var(--bgColor);
}

#top-bar {
top: 137px;
}

.top-bar::before {
background: none;
border-top: double 4px var(--accentColor);
top: -0.5rem;
}

.meta-title {
font-size: 238%;
margin: 0;
padding: 0;
}

.meta-title p {
  border-bottom: none;
}

#top-bar .open-menu a {
border: solid 3px var(--bgColor);
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
}

#header h1 a::before, #header h2::before, h1, h2, h3, h4, h5, h6 {
color: transparent;
background: linear-gradient(45deg, rgb(242, 220, 122) 0%, var(--accentColor) 50%, rgb(242, 220, 122) 100%);
-webkit-background-clip: text;
background-clip: text;
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
}

#main-content h1, #main-content h2, #main-content h3, #main-content h4, #main-content h5, #main-content h6 {
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.6));
}

@media (min-width: 768px) {
#page-content {
  font-size: inherit;
}
}

.bt.bb, .bt.bh {
filter: none;
}

