.byline span:nth-of-type(2)::after {
content: " ";
}

.byline span:nth-of-type(2) {
display: none;
}

.bt {
  filter: none;
}

:target {
    display: block;    
    position: relative;     
    top: -100px;
    visibility: hidden;
}

#page-content table.wiki-content-table {
width: 100%;
}

#page-content table.wiki-content-table th {
font-family: 'Germania One', sans-serif;
font-size: 1.1rem;
letter-spacing: 2px;
font-weight: normal;
}

.limit br {
display: unset;
}

a.initbutn:hover {
filter: drop-shadow(0px 0px 50px var(--accentColor));
}

.w-container {
    background-color: #0f5717;
    border: none;
    border-left: groove 0.4rem var(--accentColor);
    border-right: groove 0.4rem var(--accentColor);
    padding: 16px;
    margin-top: 12px;
    margin-bottom:12px;
    box-shadow: none;
    color: #fdfdfd;
    filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
}

.w-container strong {
  color: var(--textColor);
  filter: none;
}

#page-content .cell-container-image {
    background: url(https://scp-wiki.wikidot.com/local--files/scp-7006/radix.png) center no-repeat !important;
}

@media (max-width: 706px) {
    #page-content .cell-container {
        background: none !important;
     }
}

.adventure-menu {
display: flex;
flex-direction: row;
justify-content: space-between;
margin: 0;
}

.adventure-card {
border: groove 7px var(--accentColor);
margin: 10px 0.5rem;
width: 100%;
background: linear-gradient(180deg, var(--fgColor) 0%, var(--bgColor) 40%);
display: block;
box-shadow: inset 0px 0px 27px 5px rgba(0, 0, 0, 0.5);
}

.adventure-card .image-container {
padding: unset;
}

.adventure-card .image-container a {
border-bottom: none !important;
}

.adventure-card .image-container a:hover {
background-color: unset;
}

.adventure-card .image-container a img {
transition-duration: 0.5s;
filter: drop-shadow(0px 10px 10px #000);
}

.adventure-card .image-container:hover a img {
filter: drop-shadow(0px 0px 10px var(--accentColor));
}

.adventure-card.securement .image-container:hover a img {
transform: scale(0.9) rotate(-10deg);
}

.adventure-card.containment .image-container:hover a img {
transform: scale(0.9) translateY(-20px);
}

.adventure-card.protection .image-container:hover a img {
transform: scale(0.9) rotate(10deg);
}

.adventure-card.securement .image-container a::before {
content: "Securement";
font-family: var(--header-font);
display: inline-block;
padding-top: 10px;
color: var(--accentColor);
font-size: 1.5rem;
}

.adventure-card.containment .image-container a::before {
content: "Containment";
font-family: var(--header-font);
display: inline-block;
padding-top: 10px;
color: var(--accentColor);
font-size: 1.5rem;
}

.adventure-card.protection .image-container a::before {
content: "Protection";
font-family: var(--header-font);
display: inline-block;
padding-top: 10px;
color: var(--accentColor);
font-size: 1.5rem;
}

