[[module CSS]]
@import url(https://fonts.googleapis.com/css2?family=Quintessential&display=swap);
@import url('https://fonts.googleapis.com/css2?family=Long+Cang&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@500;600&display=swap');

/* HEADER */
 :root {
   --header-title: "全线战争";
   --header-subtitle: "我们需要一艘更大的船";
   --titleColor: hsl(252, 6%, 69%);
   --subtitleColor: hsl(252, 6%, 69%);
   --lgurl: url(https://scp-wiki.wdfiles.com/local--files/theme%3Awar-on-all-fronts-sigma/mksjrpride.png);

   --header-font: "Montserrat", "Arial", sans-serif;
   --strong-blue: hsl(245, 100%, 37%);

   --fnColor: var(--strong-blue);
}

 #header::before {
     opacity: .83;
}

 #header h1 a::before,
 #header h2::before {
     font-family: "Saira Stencil One", cursive;
     font-variant: small-caps;
}

 #header h1 a::before {
     text-shadow: 3px 3px 5px hsl(0, 0%, 0%);
}

 #header h2::before {
     text-shadow: 1px 1px 1px hsla(0, 0%, 0%, .8);
}

 #page-title,
 #breadcrumbs{
     display: none;
}

 .meta-title {
     color: hsl(238, 39%, 28%);
     border-color: hsl(238, 39%, 28%);
     font: small-caps 1.95rem "Saira Stencil One", cursive;
     text-align: center;
}

/* FORMATTING */
 .scp-image-block {
     box-shadow: none;
}

 .w-container {
     box-shadow: none !important;
}

 h1, h2, h3, h4, h5, h6 {
     font-family: var(--header-font);
     font-weight: bold;
}

tt {
     font-size: 110%;
}

/* DIV BLOCKS */
 .lite-heading {
   /* -- Special Heading Area -- */
     margin: 3rem -5.5%;
     padding: 0 5.5%;
     background: hsl(0, 0%, 90%);
}

 .lite-heading hr {
     width: unset;
     height: .3rem;
     margin: 0 -5.5%;
     background:
        linear-gradient(
          to right,
          hsl(0, 0%, 0%),
          hsl(257, 100%, 16%),
          hsl(240, 100%, 20%),
          hsl(257, 100%, 16%),
          hsl(0, 0%, 0%));
     border: 0;
     border-radius: 5px;
}

 .entry {
     margin: .8rem 0;
     padding: .8rem;
     background: hsl(0, 100%, 100%);
     border: .0625rem solid hsla(0, 0%, 5%, .15);
     border-left: .25rem solid hsl(245, 100%, 26%);
}

 .entry.sep {
     margin: -10px 0;
     padding: 5px;
     background: hsla(245, 100%, 37%, .15);
     border: .0625rem solid hsla(0, 0%, 5%, .15);
}

 blockquote,
 div.blockquote {
     border: 1px dashed #999;
}

 .high-king {
     margin-bottom: 1em;
     padding: 1em;
     background: hsl(222, 25%, 86%);
     border: 3px outset hsl(222, 40%, 68%);
     text-align: center;
}

 .high-king hr {
     border-color: hsl(222, 40%, 68%);
}

 .narration.book {
     padding: 1rem 2rem;
     font: 135% "Garamond", serif;
     letter-spacing: .02rem;
}

 @media (min-width: 768px) {
     .narration.book {
               margin: 2rem;
    }
}

/* COLLAPSIBLES */
 .centeredbonus .collapsible-block-folded,
 .centeredbonus .collapsible-block-unfolded-link {
     padding: .53rem .27rem;
}

 .centeredgen .collapsible-block-link,
 .centeredbonus .collapsible-block-link {
     padding-bottom: 1px;
     border-bottom: 1px solid var(--strong-blue);
     text-decoration: 0 !important;
}

 .centeredgen .collapsible-block-link {
     font: bold .95rem var(--header-font);
}

 .centeredbonus .collapsible-block-link {
     font: bold 1.3rem var(--header-font);
}

 @media (max-width: 767px){
     .centeredbonus .collapsible-block-link {
          font-size: 1.15rem;
    }
}

 .centeredbonus .collapsible-block-folded:hover,
 .centeredbonus .collapsible-block-unfolded-link:hover {
     background:
        linear-gradient(
          to right,
          transparent 0% 15%,
          hsla(245, 100%, 37%, .15) 30%,
          hsla(245, 100%, 37%, .15) 70%,
          transparent 85% 100%);
}

 .centeredgen .collapsible-block-link:hover,
 .centeredbonus .collapsible-block-link:hover {
     border-bottom-width: 2px;
}

 .centeredbonus .collapsible-block-folded a.collapsible-block-link {
     animation: blink .8s ease-in-out infinite alternate;
}

 @keyframes blink {
     0%{
          color: transparent;
          border-bottom-color: transparent;
    }

     50%, 100% {
          color: var(--strong-blue);
          border-bottom-color: var(--strong-blue);
    }
}
[[/module]]

