[[module CSS]]
/* Styling for the email container */
.email-container {
    background-color: #f7f7f7;
    padding: 20px;
    border: 1px solid #ccc;
    border-radius: 5px;
    margin: auto;
    font-family: Arial, sans-serif;
    text-align: center; /* Center align buttons */
    position: relative;
    width: 90%;
}
.body {
    padding: 0px;
    margin: 0px;
}

/* Styling for the quote block */
.quote-block {
    background-color: #f7f7f7; /* Background color */
    border-left: 4px solid #0077cc; /* Left border color */
    padding: 10px 20px; /* Padding inside the quote block */
    margin: 10px 0; /* Margin around the quote block */
    font-style: italic; /* Italic text */
    color: #333; /* Text color */
    text-align: left;
}

.email {
   text-align: left;
}

/* Styling for email items */
.email-item {
    background-color: #fff;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    padding: 10px;
    border-radius: 3px;
    color: #555; /* Slightly darker text color */
    text-align: center;
}

/* Styling for email headers */
.email-header {
    text-align: center;
    font-size: 24px;
    font-weight: bold;
    margin-bottom: 10px;
    color: #333; /* Darker title color */
}

/* Styling for email actions */
.email-actions {
    position: relative;
    left: 0; /* Align buttons to the left */
    bottom: 10px;
    width: 100%; /* Ensure full width for buttons */
}

.email-action {
    display: inline-block; /* Display buttons next to each other */
    margin: 0 10px; /* Add margin between buttons */
    text-decoration: none;
    color: #0077cc;
    font-weight: bold;
    pointer-events: none;
}

.email-action:hover {
    background-color: #0077cc; /* Change background color on hover */
    color: #fff; /* Change text color on hover */
    cursor: pointer; /* Change cursor to pointer on hover */
    text-decoration: none; /* Remove underline on hover */
}

/* Styling for email sender, subject, and date */
.email-sender,
.email-subject,
.email-date {
    display: block;
    margin-bottom: 5px;
    margin-top: 10px;
    font-weight: bold;
}

.email-subject {
     color: #c46262;
{

/* Styling for the new message container */
#new-message-1 {
    background-color: #fff;
    margin-bottom: 10px;
    border: 1px solid #ddd;
    padding: 10px;
    border-radius: 3px;
    color: #555; /* Slightly darker text color */
}
