[[module css]]
:root{
  --header-title: "SCP基金会";
  --header-subtitle: "控制 · 收容 · 保护";
  --head-font: var(--ui-font);
  --page-font-size: 0.79rem;
  --link-txt-color: rgba(var(--accent), 0.75);

  --accent: 0, 77, 99;

  --posX: calc(50% - 358px - 17.5rem);
  --fnColor: rgb(var(--accent));
}

h1, h2, h3, h4, h5, h6 {
  font-weight: 900;
}

#page-content > p {
  font-weight: normal;
}

#page-content .blockquote {
  margin: 1em 2em;
}

#page-content hr {
  height: 1px;
  background-color: rgb(var(--accent));
  margin-top: 1.5em;
  margin-bottom: 1.5em;
}

#page-content .modal {
  font-family: 'Inter';
  font-size: 110%;
}

#page-content .notation {
  width: auto;
  margin: auto;
  font-family: 'Inter';
  font-size: 110%;
  border-left: solid 4px rgba(var(--accent), 0.33);
  border-right: solid 4px rgba(var(--accent), 0.33);
}

#page-content .jotting {
  padding: 0.5em 1.5em;
}

.notation > .jotting,
#page-content > .jotting > blockquote {
  background-color: white;
}

#page-content a {
  font-weight: bold;
}

.fncon {
  font-family: var(--page-font) !important;
  font-size: 92% !important;
  line-height: 1.4;
  border-color: rgb(var(--accent));
}

.fncon::before {
  font-family: 'Inter';
}

@media only screen and (min-width: 1278px) {
  .fncon {
    width: 14rem;
    border: none;
    border-left: 5px rgb(var(--accent)) solid;
    background-color: rgba(var(--accent), 0.1);
  }

  .fncon::before {
    top: 14px; left: -10px;
  }
}

@media (max-width: 767px) {
  .jotting,
  .modal {
    margin: 1em;
  }   
}

#page-content .scp-image-block img {
  border: solid 1px rgba(var(--accent), 0.66);
}

#page-content .scp-image-block.block-left {
  margin: 0.5em 1.5em 1em 0em;
}

#page-content .scp-image-block.block-right {
  margin: 0.5em 0em 1em 1.5em;
}

#page-content .scp-image-caption {
  border: solid 1px rgba(var(--accent), 0.66);
  border-radius: 0px;
  margin-top: 7px;
}
[[/module]]

