    overflow: hidden;
    background-color: lightblue;
    position: relative;
    transition: --num 5s;
    counter-set: num var(--num);
    font: 300 1em 'PO', "Outfit", 'OppoSans', 'MiSans L3', sans-serif;
    text-align: center;
    margin: auto;
    width: 150px;
    height: 150px;
    padding: 5px 5px;
    background-color: #f0f6f0;
    color: #364343;
    border-radius: 2px;
    border: 3px solid black;
    cursor: wait;
    display: block;
    -webkit-transition-duration: 0.4s;
    transition-duration: 0.4s;
}

.hoverer::after {
    content: counter(num);
    font-weight: bold;
    font-size: 1.2em;
}

.hoverer:hover {
    color: #222323;
    display: block;
    transition: all 5s;

}

.bar-container {
    width: 200px;
    height: 5px;
    background-color: #fafbf6;
    opacity: .8;
    border-radius: 10px;
    overflow: visible;
    color: transparent;
    position: relative;
    margin: 20px auto;

}

.hoverer p {
    Left: 50%;
    Top: 50%;
    font-weight: bold;
    font-size: 1.2em;
}

.loading-bar, .loading-bar2 {
    max-width: 200px;
    width: 0px;
    color: transparent;
    height: 5px;
    background-color: rgb(0, 0, 255);
    border-radius: inherit;
    transition: width 5s linear;
    opacity: .7;
    display: block;
    margin: 0 auto;
}
.loading-bar { background-color: rgb(0, 0, 255); }
.loading-bar2 { background-color: rgb(14, 14, 18); }
.hiddens,
.msDiv1,
.msDiv2,
.msDiv3,
.msDiv4,
.msDiv5,
.msDiv6,
.msDiv7,
.msDiv8,
.msDiv9,
.msDivFin {
    padding-inline: 10px;
    padding-bottom: 100px;
    display: none;
    margin: 0px;
    color: black;
    font-size: .9rem;
    font-family: 'PO', 'Outfit', 'OppoSans', 'MiSans L3', sans-serif;
}

@keyframes loading {
    0% {
     width: 0;
    }

    100% {
     width: 100%;
    }
}

.hoverer:hover+.bar-container .loading-bar, .hoverer:hover+.bar-container .loading-bar2 {
    animation: loading 5s linear infinite;
}

button {
    font: inherit;
    background-color: black;
    color: rgb(137, 137, 137);
    border-radius: 0.5em;
    font-size: .8rem;
    padding: 0.375em 1em;
    font-weight: 300;
    text-shadow: 0 0.0625em 0 #fff;
    box-shadow: inset 0 0.0625em 0 0 #f4f4f4, 0 0.0625em 0 0 #efefef,
     0 0.125em 0 0 #ececec, 0 0.25em 0 0 #e0e0e0, 0 0.3125em 0 0 #dedede,
     0 0.375em 0 0 #dcdcdc, 0 0.425em 0 0 #cacaca, 0 0.425em 0.5em 0 #cecece;
    transition: 0.15s ease;
    width: 50px;
    height: 50px;
    border: 1px solid black;
    text-align: center;
    cursor: pointer;
}


button:active {
    translate: 0 0.225em;
    box-shadow: inset 0 0.03em 0 0 #f4f4f4, 0 0.03em 0 0 #efefef,
     0 0.0625em 0 0 #ececec, 0 0.125em 0 0 #e0e0e0, 0 0.125em 0 0 #dedede,
     0 0.2em 0 0 #dcdcdc, 0 0.225em 0 0 #cacaca, 0 0.225em 0.375em 0 #cecece;
    text-align: center;
}

.row {
    flex-wrap: wrap;
    padding: 0 4px;
    justify-content: space-around;
    width: 150px;
    margin: 0 auto;
    opacity: 0;
    transition: opacity 0.5s ease-in-out;
    display: flex;
}

.fade-in {
    row.element.style.display='flex';
    opacity: 1;
}

.column {
    flex: 45%;
    max-width: 50px;
    margin: auto;
    border-radius: 50%;
    height: 50px;
    text-align: center;
    display: flex;
    justify-content: space-around;
    background-size: cover;
}

.disable {
    filter: blur(5px) grayscale(100%);
    opacity: 0.2;
    pointer-events: none;
    cursor: not-allowed;
}

.prestige {
    counter-set: prestige var(--prestige);
}

.kismet{
    counter-set: prestige var(--kismet);
}

.tokens {
    counter-set: prestige var(--tokens);
}

#prestige {
    background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/prestige.png");
    background-size: cover;
}

#plustwomulti {
    background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/drinkperpres.png");
    background-size: cover;
}

#autodrink {
    background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/autodrink.png");
    background-size: cover;
}

#stop {
    background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/stop.png");
    background-size: cover;
}

#reset {
    background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/reset.png");
    background-size: cover;
    top: 0;
    right: 0;
    width: 50px;
    height:50px;
    display: block;
    position: relative;
}
.boxTitle, #prestigeCounter, #drinkSpeedCounter, #kismetCounter, #tokenCounter {
    font-family: 'PO', "Outfit", 'OppoSans', 'MiSans L3', sans-serif;
    font-size: .8em;
    border-bottom: .05rem;
    border-color: black;
    display: block;
    position: relative;
    margin: 0 auto;
    text-align: center;
    width: 100%;
}



.messageWarn {
    background-image: url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23d7d6d8' fill-opacity='0.4' fill-rule='evenodd'%3E%3Cpath d='M0 40L40 0H20L0 20M40 40V20L20 40'/%3E%3C/g%3E%3C/svg%3E");
    font-family: 'PO', "Outfit", 'OppoSans', 'MiSans L3', sans-serif;
    font-size: .8em;
    width: 100%;
    padding: 4px;
    border-block: 1px solid black;
    text-align: center;
    left: 0;
    right: 0;
}

.rules1.disable,
.rules2.disable,
.rules3.disable,
.rules4.disable {
    -webkit-user-select: none;
    /* Safari */
    -ms-user-select: none;
    /* IE 10 and IE 11 */
    user-select: none;
    /* Standard syntax */
    cursor: not-allowed !important;
}

.buttonholder {
height: 80px;
width: 100%;
background-color: #c6c6c6;
background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 0h20L0 20z' fill='%23ffffff' fill-opacity='0.4' fill-rule='evenodd'/%3E%3C/svg%3E");
display: flex;
justify-content: center;
}

.buttonholder button {
top: 22%;
position: relative;
}


.achievement-container {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: 100%;
  max-width: 800px;
  margin: 0 auto;
  gap: 10px;
}

.image-slot {
  width: calc(11.5% - 10px);
  aspect-ratio: 1;
  background-color: #f0f0f0;
  border: 2px solid #ccc;
  display: flex;
  justify-content: center;
  align-items: center;
}

.image-slot img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform .2s;
}


.image-slot img:hover {
  transform: scale(1.5);
}
.title-screen {
        top:55px;
        right:0;
        left: 0;
    position: absolute;
    margin: 0 auto;
    text-align: center;
        display: none;
    width: 150px;
    height: 150px;
