const loadingBar = document.querySelector('.loading-bar');
const loadingBar2 = document.querySelector('.loading-bar2');
const prestigeOnce = document.getElementById('prestige');
const hiddenDiv = document.querySelector('.hiddens');
const titleBox = document.querySelector(".boxTitle");


const mileStone1 = 8;
const mileStone2 = 24;
const mileStone3 = 55;
const mileStone4 = 184;
const mileStone5 = 269;
const mileStone6 = 383;
const mileStone7 = 447;
const mileStone8 = 606;
const mileStone9 = 716;
let maxNum = 1000;
let growthRate = 1.15;
let num = 0;
let incInt = null;
let incDel = 5000;
