let r = 0;
let g = 0;
let b = 255;
let x = 0;
let y = 0;
let z = 0;
let prestige = 0;
let multiplier = 1 + prestige;
let kismet = 1;
let mod = 2;
let outsideCoin = 0;
let remainingDrinkers = 1;
let autoDrinkInterval = null;
let rulesActive = [0,0,0,0];
let rowState = 0;
let achievementState = [0,0,0,0,0,0,0,0];
let button = [
    document.querySelector("#unlock-1"),
    document.querySelector("#unlock-2"),
    document.querySelector("#unlock-3"),
    document.querySelector("#unlock-4")
];

let gameData = [
    num,
    incInt,
    incDel,
    autoDel,
    r,
    g,
    b,
    prestige,
    remainingDrinkers,
    autoDrinkInterval,
    rulesActive,
    achievementState,
    rowState,
    outsideCoin,
    maxNum,
    growthRate,
    kismet
];

const reset = document.getElementById('reset');
const plusMod = document.getElementById('plustwomulti');
const drinker = document.getElementById('autodrink');
const exit = document.getElementById('stop');
const rulesOne = document.querySelector('.rules1');
const rulesTwo = document.querySelector('.rules2');
const rulesThree = document.querySelector('.rules3');
const rulesFour = document.querySelector('.rules4');
const rowArray = document.querySelectorAll('.row');
const controller= document.querySelector('.controls');
const leveller = document.getElementById('levelCounter');
const scorecard = document.getElementById('scoreCounter');
const untitledDrinker = document.getElementById('game');
const gametitleScreen= document.getElementById('titleScreen');
const milestones = [{
        value: mileStone1,
        element: msDiv1
    },
    {
        value: mileStone2,
        element: msDiv2
    },
    {
        value: mileStone3,
        element: msDiv3
    },
    {
        value: mileStone4,
        element: msDiv4
    },
    {
        value: mileStone5,
        element: msDiv5
    },
    {
        value: mileStone6,
        element: msDiv6
    },
    {
        value: mileStone7,
        element: msDiv7
    },
    {
        value: mileStone8,
        element: msDiv8
    },
    {
        value: mileStone9,
        element: msDiv9
    },
];
let lastMilestoneIndex = -1;



function setWaterQuality() {
    r++;
    g++;
    b--;

    let delta = 255 - Math.abs(b);
    let newB = 255 - (delta / 1.15);


    x = r / 4;
    y = g / 2;
    z = newB;


    var newQuality = ` rgb(${x}, ${y}, ${z})`;
    loadingBar.style.backgroundColor = newQuality;
}

function resetWaterQuality() {
    x = 0;
    y = 0;
    z = 255;
    r = 0;
    g = 0;
    b = 255;

    var newQuality = ` rgb(${x}, ${y}, ${z})`;
}

function incre() {

    if (incInt !== null) {
        clearInterval(incInt);
    }

    incInt = setInterval(() => {
        if (num < maxNum) {
            num++;
            setWaterQuality();
            updateGameData();
            updateCounters();
            if (num >= 1) {
                for (let i = 0; i < 3; i++) {
                    rowArray[i].classList.add("fade-in");

                }
                rowState = 1;

            }
            mileStoneCheck();
        } else if (num >= maxNum && prestigeOnce.classList.contains("disable")) {

            alert("你感到眩晕……");
            resetGameState();
            location.reload();
        }

    }, incDel);
}

function resetGameState() {

    num = 0;
    incInt = null;
    incDel = 5000;
    autoDel = 5000;
    r = 0;
    g = 0;
    b = 255;
    prestige = 0;
    maxNum= 1000;
    remainingDrinkers = 1;
    autoDrinkInterval = null;
    rulesActive = [0, 0, 0, 0];
    rowState = 0;
    achievementState = [0, 0, 0, 0, 0, 0, 0, 0];
    updateCounters();
    resetMilestones();
    resetWaterQuality();
    resetButtons();
    resetRows();
    hiddenDiv.style.display = 'none';
    div.style.setProperty('--prestige', prestige);
    div.style.setProperty('--num', num);
    div.style.setProperty('--kismet', kismet);
    div.style.setProperty('--tokens', remainingDrinkers);
    updateGameData();
    manualSave();
    console.log(gameData);
    outsideCoin = 0;
    multiplier = 1;
    growthRate = 1.15;
    kismet = 1;
    mod = 2;

}


function mileStoneCheck() {

    milestones.forEach((milestone, index) => {
        if (num == maxNum) {
            div.style.setProperty('--num', num);
            clearInterval(incInt);
            loadingBar.style.animation = 'none';
            loadingBar2.style.animation = 'none';
            msDivFin.style.display = 'block';
            milestones[lastMilestoneIndex].element.style.display = 'none';
        } else if (num === milestone.value) {

            if (lastMilestoneIndex !== -1) {
                milestones[lastMilestoneIndex].element.style.display = 'none';
            }

            milestone.element.style.display = 'block';
            lastMilestoneIndex = index;
        }
    });
    div.style.setProperty('--num', num);

}

function resetMilestones() {
    milestones.forEach(milestone => {
        milestone.element.style.display = 'none';
    });
    msDivFin.style.display = 'none';
    hiddenDiv.style.display = 'none';
    lastMilestoneIndex = -1;
}


function resetRows() {
    rowArray.forEach(element => {
        element.classList.remove('fade-in');
    });
    rowState = 0;
}

div.addEventListener('mouseover', () => {
    if (num >= maxNum) {
        clearInterval(incInt);
        loadingBar.style.animation = 'none';

    } else {
        if (incInt === null) {
            let animSpeed = incDel / 1000;
            loadingBar.style.animationIterationCount = maxNum - num;
            loadingBar.style.animation = `loading ${animSpeed}s linear infinite`;
            incre();
        }
    }
});

div.addEventListener('mouseleave', () => {
    if (incInt !== null) {
        clearInterval(incInt);
        incInt = null;
    }
    loadingBar.style.animation = 'none';
});

function toggleButton(list, buttonSelect, targetElement, rulesElement) {
    buttonSelect.addEventListener("click", function() {
        targetElement.classList.remove("disable");
        targetElement.classList.add("enable");
        rulesElement.classList.remove("disable");
        for (let i = 0; i < list.length; i++) {
            if (list[i] == buttonSelect) {
                rulesActive[i] = 1;
            }
        }
    });
}
reset.addEventListener('click', function() {
    resetGameState();
});

toggleButton(button, button[0], plusMod, rulesOne);
toggleButton(button, button[1], drinker, rulesTwo);
toggleButton(button, button[2], prestigeOnce, rulesThree);
toggleButton(button, button[3], exit, rulesFour);

prestigeOnce.addEventListener('click', function() {
    if (prestigeOnce.classList.contains("enable")) {
        if (num >= maxNum) {
            const baseValue = 1000;
            num = 0;
            prestige++;
            kismet = Math.random() * 1.8 - 0.9;
            multiplier = 1 + prestige + Math.round(kismet);
            growthRate = growthRate + (1 - Math.abs(kismet) + .15);
            maxNum += baseValue * (1 - Math.abs(kismet)) + Math.abs(Math.pow(growthRate, prestige));
            remainingDrinkers = prestige + Math.round(Math.random());
