<script>
    const sounds = {
      jump: document.getElementById('jumpSound'),
      collect: document.getElementById('collectSound'),
      powerUp: document.getElementById('powerUpSound'),
      door: document.getElementById('doorSound'),
      gameOver: document.getElementById('gameOverSound'),
  };

  const background = document.getElementById('backgroundMusic');

  let decreasing = true;
  let musicvolumeLevel = 0;
  let sfxvolumeLevel = musicvolumeLevel * .75;

  function music(background) {
      background.loop = true;
      background.play();
  }

  function playSound(effect) {
      if (sounds[effect]) {
          sounds[effect].currentTime = 0;
          sounds[effect].play();
      }
  }

  function cycleVolume() {

      background.volume = musicvolumeLevel;
      Object.values(sounds).forEach(sound => {
          sound.volume = sfxvolumeLevel;
      });


      if (decreasing) {
          musicvolumeLevel -= 0.1;
          sfxvolumeLevel -= 0.1
          if (musicvolumeLevel < 0) {
              musicvolumeLevel = 0;
              sfxvolumeLevel = 0;
              decreasing = false;
          }
      } else {
          musicvolumeLevel += 0.1;
          sfxvolumeLevel += 0.1
          if (musicvolumeLevel > 1) {
              musicvolumeLevel = 1;
              sfxvolumeLevel = .7;
              decreasing = true;
          }
      }

  }
  const game = document.getElementById('game');
  const levelCounter = document.getElementById('levelCounter');
  const scoreCounter = document.getElementById('scoreCounter');
  const gameWidth = 150,
      gameHeight = 150;
  let level = 1,
      score = 0,
      waterCollected = 0,
      totalWaters = 5,
      minWaters = 3;
  let player = {
      x: 10,
      y: 130,
      width: 10,
      height: 10,
      dx: 0,
      dy: 0,
      onGround: false,
      canDoubleJump: false,
      invertedGravity: false,
      trail: []
  };
  let platforms = [],
      waters = [],
      pollutions = [],
      door = null,
      powerups = [];
  let gravity = 0.6;
  let quadrant = {
      x: 0,
      y: 0
  };

  function initLevel() {
      game.innerHTML = '';
      player.x = 10;
      player.y = 130;
      player.dx = 0;
      player.dy = 0;
      waterCollected = 0;
      player.trail = [];
      player.canDoubleJump = false;
      player.invertedGravity = false;
      platforms = generatePlatforms();
      waters = generateWaters();
      pollutions = generatePollutions();
      powerups = generatePowerups();
      door = null;
      renderLevel();
  }

  function renderLevel() {
      game.innerHTML = '';
      platforms.forEach(platform => {
          const div = document.createElement('div');
          div.classList.add('platform');
          div.style.width = platform.width + 'px';
          div.style.height = platform.height + 'px';
          div.style.left = platform.x + 'px';
          div.style.top = platform.y + 'px';
          game.appendChild(div);
      });


      const playerDiv = document.createElement('div');
      playerDiv.classList.add('player');
      playerDiv.style.left = player.x + 'px';
      playerDiv.style.top = player.y + 'px';
      game.appendChild(playerDiv);


      player.trail.forEach((trail, i) => {
          const trailDiv = document.createElement('div');
          trailDiv.classList.add('trail');
          trailDiv.style.left = trail.x + 'px';
          trailDiv.style.top = trail.y + 'px';
          trailDiv.style.backgroundColor = `hsl(${trail.hue}, 100%, 50%)`;
          trailDiv.style.opacity = trail.opacity;
          game.appendChild(trailDiv);
      });

      waters.forEach(water => {
          const div = document.createElement('div');
          div.classList.add('water');
          div.style.left = water.x + 'px';
          div.style.top = water.y + 'px';
          game.appendChild(div);
      });

      pollutions.forEach(pollution => {
          const div = document.createElement('div');
          div.classList.add('pollution');
          div.style.left = pollution.x + 'px';
          div.style.top = pollution.y + 'px';
          game.appendChild(div);
      });


      if (door) {
          const doorDiv = document.createElement('div');
          doorDiv.classList.add('door');
          doorDiv.style.left = door.x + 'px';
          doorDiv.style.top = door.y + 'px';
          game.appendChild(doorDiv);
      }


      powerups.forEach(powerup => {
          const div = document.createElement('div');
          div.classList.add('powerup');
          div.style.left = powerup.x + 'px';
          div.style.top = powerup.y + 'px';
          div.style.backgroundColor = powerup.type === 'doubleJump' ? '#ffb3ff' : '#ccffcc';
          game.appendChild(div);
      });
  }

function generatePlatforms() {
    const platforms = [];


    platforms.push({
        x: 0,
        y: 140,
        width: gameWidth,
        height: 10
    });


    const numPlatforms = Math.floor(Math.random() * 3) + 3;

    for (let i = 0; i < numPlatforms; i++) {
        const platformWidth = Math.floor(Math.random() * 40) + 20;
        const x = Math.floor(Math.random() * (gameWidth - platformWidth));
        const y = Math.floor(Math.random() * (gameHeight - 50)) + 30;

        platforms.push({
            x: x,
            y: y,
            width: platformWidth,
            height: 10
        });
    }

    return platforms;
}

  function generateWaters() {
      const waters = [];
      for (let i = 0; i < totalWaters; i++) {
          const x = Math.random() * (gameWidth - 10);
          const y = Math.random() * (gameHeight - 10);
          waters.push({
              x,
              y
          });
      }
      return waters;
  }

  function generatePollutions() {
      const pollutions = [];
      for (let i = 0; i < level; i++) {
          const x = Math.random() * (gameWidth - 10);
          const y = Math.random() * (gameHeight - 10);
          pollutions.push({
              x,
              y
          });
      }
      return pollutions;
  }

  function generatePowerups() {
      return [{
              x: Math.random() * (gameWidth - 10),
              y: Math.random() * (gameHeight - 10),
              type: 'doubleJump'
          },
          {
              x: Math.random() * (gameWidth - 10),
              y: Math.random() * (gameHeight - 10),
              type: 'invertedGravity'
          }
      ];
  }

  function nextLevel() {
      level++;
      levelCounter.innerText = '等级：' + level;
      initLevel();
  }

  function gameOver() {
      playSound('gameOver');

      level = 1;
      score = 0;
      waterCollected = 0;
      scoreCounter.innerText = '得分：0';
      levelCounter.innerText = '等级：1';
      initLevel();
  }

  function update() {
      player.dy += player.invertedGravity ? -gravity : gravity;
      player.x += player.dx;
      player.y += player.dy;

      if (player.x < 0) {
          quadrant.x;
          player.x = gameWidth - player.width;
      } else if (player.x + player.width > gameWidth) {
          quadrant.x++;
          player.x = 0;
      }
      if (player.y < 0) {
          quadrant.y;
          player.y = gameHeight - player.height;
      } else if (player.y + player.height > gameHeight) {
          quadrant.y++;
          player.y = 0;
      }


      platforms.forEach(platform => {
          if (player.y + player.height > platform.y && player.y < platform.y + platform.height &&
              player.x + player.width > platform.x && player.x < platform.x + platform.width) {
              if (player.dy > 0 && player.y < platform.y) {
                  player.onGround = true;
                  player.dy = 0;
                  player.y = platform.y - player.height;
              }
          }
      });


      waters = waters.filter(water => {
          const collected = (player.x < water.x + 10 && player.x + player.width > water.x &&
              player.y < water.y + 10 && player.y + player.height > water.y);
          if (collected) {
              score++;
              scoreCounter.innerText = '得分：' + score;
              waterCollected++;
              playSound('collect');
          }
          return !collected;
      });

      if (waterCollected === minWaters && !door) {
          door = {
              x: Math.random() * (gameWidth - 20),
              y: Math.random() * (gameHeight - 20)
          };
      }


      if (door && player.x < door.x + 15 && player.x + player.width > door.x &&
          player.y < door.y + 20 && player.y + player.height > door.y) {
          playSound('door');
          nextLevel();
      }

      pollutions.forEach(pollution => {
          if (player.x < pollution.x + 10 && player.x + player.width > pollution.x &&
              player.y < pollution.y + 10 && player.y + player.height > pollution.y) {
              gameOver();
          }
      });


      powerups = powerups.filter(powerup => {
          const collected = (player.x < powerup.x + 10 && player.x + player.width > powerup.x &&
              player.y < powerup.y + 10 && player.y + player.height > powerup.y);
          if (collected) {
              if (powerup.type === 'doubleJump') {
                  player.canDoubleJump = true;
                  playSound('powerUp');
                  setTimeout(() => player.canDoubleJump = false, 5000);
              } else if (powerup.type === 'invertedGravity') {
                  player.invertedGravity = true;
                  playSound('powerUp');
                  setTimeout(() => player.invertedGravity = false, 5000);
              }
          }
          return !collected;
      });


      player.trail.unshift({
          x: player.x,
          y: player.y,
          hue: Math.random() * 360,
          opacity: 1
      });
      if (player.trail.length > 20) player.trail.pop();
      spreadPollution();
      renderLevel();
      requestAnimationFrame(update);
  }
  const titleScreen = document.getElementById('titleScreen');

  function startGame() {
      musicvolumeLevel = 1;
      sfxvolumeLevel = musicvolumeLevel * .75;
      music(background);
      cycleVolume();
      titleScreen.style.opacity = '0';
      initLevel();
      update();
  }


let moveIncrement = 2;
let movementDelay = 100;
let lastMoveTime = 0;
let moving = false;

// Keyboard Controls
document.addEventListener('keydown', (e) => {
    if (e.key === 'Enter') {
        startGame();
    }
    if (['ArrowRight', 'ArrowLeft', 'ArrowUp', 'ArrowDown'].includes(e.key)) {
        e.preventDefault();
    }
    if (e.key === 'ArrowRight') movePlayer('right');
    if (e.key === 'ArrowLeft') movePlayer('left');
    if (e.key === 'ArrowUp' && player.onGround) {
        player.dy = -8;
        player.onGround = false;
        playSound('jump');
    } else if (e.key === 'ArrowUp' && player.canDoubleJump) {
        player.dy = -8;
        player.canDoubleJump = false;
    }
    if (e.key === 'ArrowDown' && player.canGlide) player.dy = 1;
});

document.addEventListener('keyup', (e) => {
    if (e.key === 'ArrowRight' || e.key === 'ArrowLeft') stopPlayer();
});

// Touch and Mouse Controls for D-pad
document.querySelectorAll('.button[data-action]').forEach(button => {
    button.addEventListener('touchstart', e => {
        e.preventDefault();
        const action = e.target.getAttribute('data-action');
        movePlayer(action);
    });
    button.addEventListener('mousedown', e => {
        e.preventDefault();
        const action = e.target.getAttribute('data-action');
        movePlayer(action);
    });
    button.addEventListener('mouseup', () => stopPlayer());
    button.addEventListener('touchend', () => stopPlayer());
});

function movePlayer(action) {
    const currentTime = Date.now();
    if (currentTime - lastMoveTime < movementDelay) return;
    lastMoveTime = currentTime;

    if (action === 'up' && player.onGround) {
        player.dy = -8;
        playSound('jump');
        player.onGround = false;
    } else if (action === 'down') {
        player.dy += moveIncrement;
    } else if (action === 'left') {
        player.dx = -moveIncrement;
    } else if (action === 'right') {
        player.dx = moveIncrement;
    } else if (action === 'sound') {
        cycleVolume();
    } else if (action === 'enter') {
        startGame();
        music(background);
    }
}

function stopPlayer() {
    player.dx = 0;
}

  function spreadPollution() {
      const newPollutions = [];
      pollutions.forEach(pollution => {


          if (Math.random() < 0.01) {
              const directions = [{
                      dx: -10,
                      dy: 0
                  },
                  {
                      dx: 10,
                      dy: 0
                  },
                  {
                      dx: 0,
                      dy: -10
                  },
                  {
                      dx: 0,
                      dy: 10
                  }
              ];
              const direction = directions[Math.floor(Math.random() * directions.length)];
              const newX = pollution.x + direction.dx;
              const newY = pollution.y + direction.dy;


              const minX = 0;
              const maxX = gameWidth - 10;
              const minY = 0;
              const maxY = gameHeight - 10;


              if (newX >= minX && newX <= maxX &&
                  newY >= minY && newY <= maxY &&
                  !pollutions.some(p => p.x === newX && p.y === newY)) {
                  newPollutions.push({
                      x: newX,
                      y: newY
                  });
              }
          }
      });


      pollutions.push(...newPollutions);
  }
