	margin: 4px auto;
	border-radius: 50%;
	height: 45px;
	text-align: center;
	display: flex;
	justify-content: space-around;
	background-size: cover;
}

.disable {
	filter: blur(5px) grayscale(100%);
	opacity: 0.2;
	pointer-events: none;
	cursor: not-allowed;
}

#prestige {
	background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/prestige.png");
	background-size: cover;
}

#plustwomulti {
	background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/drinkperpres.png");
	background-size: cover;
}

#autodrink {
	background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/autodrink.png");
	background-size: cover;
}


#stop {
	background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/stop.png");
	background-size: cover;
}

#reset {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/reset.png");
  background-size: cover;
  top: 300px; 
}

#kismet {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/kismet.png");
  background-size: cover;
  top:150px;
}

#help {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/help.png");
  background-size: cover;
  top: 100px;
}

#delete {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/delete.png");
  background-size: cover;
  top: 350px;
}

#hotline {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/hotline.png");
  background-size: cover;
  top: 200px;
}

#save {
  background-image: url("http://scpdsandbox.wikidot.com/local--files/billith9/save.png");
  background-size: cover;
  top: 50px;
}


#reset, #kismet, #help, #delete, #hotline, #save {
  z-index:25;
  position: absolute;
  left: -60px;
  width: 50px;
  height: 50px;
  display: block;
  transition: left 0.5s ease;

}

#reset:hover, #kismet:hover, #help:hover, #delete:hover, #hotline:hover, #save:hover {
left:0;
}

.reveal {
  animation: slideIn 0.5s forwards;
  left: 0;
}


@keyframes slideIn {
  from {
    left: -60px;
  }
  to {
    left: 0;
  }
}


.boxTitle, #prestigeCounter, #drinkSpeedCounter, #kismetCounter, #tokenCounter, #LDCounter, .hintBox { 
	font-family: "Outfit", sans-serif;
	font-size: .8em;
	border-bottom: .05rem;
	border-color: black;
	display: block;
	position: relative;
	margin: 0 auto;
	text-align: center;

}

 #prestigeCounter, #drinkSpeedCounter, #kismetCounter, #tokenCounter, #LDCounter { width: 70%; }
.boxTitle, .hintBox { width: 100%; }
.messageWarn {
	background-image: url("data:image/svg+xml,%3Csvg width='40' height='40' viewBox='0 0 40 40' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='%23d7d6d8' fill-opacity='0.4' fill-rule='evenodd'%3E%3Cpath d='M0 40L40 0H20L0 20M40 40V20L20 40'/%3E%3C/g%3E%3C/svg%3E");
	font-family: "Outfit", sans-serif;
	font-size: .8em;
	width: 100%;
	padding: 4px;
	text-align: center;
	left: 0;
	right: 0;
        border-block: 1px solid black;
}

.hintBox{
	font-family: "Outfit", sans-serif;
	font-size: .8em;
        border: 2px dashed black;
}

.hint {

flex-wrap: wrap;
padding: 0 4px;
justify-content: space-around;
width: 150px;
margin: 0 auto;
transition: opacity 0.5s ease-in-out;
display: flex;

}

.hintWrap {

max-width: 100px;
margin: 5px auto;
display: flex;
justify-content: space-between;
text-align:center; 
flex:25%;
margin-inline:4px;

}


.hint.disable {
	-webkit-user-select: none;
	/* Safari */
	-ms-user-select: none;
	/* IE 10 and IE 11 */
	user-select: none;
	/* Standard syntax */
	cursor: not-allowed !important;
}

.button-3d, .button-2d {
  display: inline-block;
  padding: 7px;
  border-radius: 50%;
  background: linear-gradient(145deg, #ffffff, #d4d4d4);
  box-shadow: 4px 4px 8px #aaa, -4px -4px 8px #ffffff;
  transition: box-shadow 0.2s, transform 0.2s;
  cursor: help;
}

.button-3d:active {
  box-shadow: inset 4px 4px 8px #aaa, inset -4px -4px 8px #ffffff;
  animation: bounce-back 0.3s ease;
}

.button-2d:active {

   box-shadow: inset 4px 4px 8px #aaa, inset -4px -4px 8px #ffffff;
  transform: scale(0.95);
}

.buttonholder {
height: 80px;
width: 100%;
background-color: #c6c6c6;
background-image: url("data:image/svg+xml,%3Csvg width='20' height='20' viewBox='0 0 20 20' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 0h20L0 20z' fill='%23ffffff' fill-opacity='0.4' fill-rule='evenodd'/%3E%3C/svg%3E");
display: flex;
justify-content: center;
}

.buttonholder button {
top: 22%;
position: relative;
}

.achievement-container {
  display: flex;
  flex-wrap: wrap; 
  justify-content: space-around;
  width: 100%;
  max-width: 800px; 
  margin: 0 auto; 
  gap: 10px; 
}

.image-slot {
  width: calc(11.5% - 10px); 
  aspect-ratio: 1; 
  background-color: #f0f0f0; 
  border: 2px solid #ccc;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-block: 10px;
}

.image-slot img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform .2s;
}


.image-slot img:hover {
  transform: scale(1.5);
}



