	z-index: 11;
}

.rgb {
	animation: colorRotate 1s linear 0s infinite;
}


@keyframes colorRotate {
	from {
		color: #6666ff;
	}

	10% {
		color: #0099ff;
	}

	50% {
		color: #00ff00;
	}

	75% {
		color: #ff3399;
	}

	100% {
		color: #6666ff;
	}
}

.instructions {
	font-size: .7em;
	background-color: rgba(169, 169, 169, .5);
}

.controls {
	margin-top: 20px;
	display: none;
	flex-direction: column;
	align-items: center;
	position: relative;
	margin-inline: auto;
}

.d-pad {
	display: grid;
	grid-template-columns: repeat(3, 50px);
	grid-template-rows: repeat(3, 50px);
	gap: 5px;
    	font-family: "Outfit", sans-serif;
}

.button {
	width: 50px;
	height: 50px;
	background-color: #666;
	border-radius: 5px;
	display: flex;
	justify-content: center;
	align-items: center;
	color: white;
	font-size: 18px;
	user-select: none;
     	font-family: "Outfit", sans-serif;
}

.button:active {
	background-color: #333;
}



.parent {
	margin: 0 auto;
	display: flex;
	justify-content: center;
	flex-direction: column;
	z-index: 9;
}

#game {
	position: relative;
	width: 150px;
	height: 150px;
	background-color: #ddd;
	display: none;
	justify-content: center;
	flex-direction: column;
}

.platform,
.player,
.water,
.pollution,
.door,
.powerup {
	position: absolute;
}

.platform {
	background-color: #bfbfbf;
}

.water {
	background-color: #00f;
	width: 10px;
	height: 10px;
}

.pollution {
	background-color: #000;
	width: 10px;
	height: 10px;
}

.player {
	width: 10px;
	height: 10px;
	background-color: #0f0;
}

.door {
	background-color: #ff0;
	width: 15px;
	height: 20px;
}

.powerup {
	width: 10px;
	height: 10px;
	border-radius: 50%;
}

.trail {
	position: absolute;
	width: 10px;
	height: 10px;
	background-color: red;
	opacity: 1;
	transition: opacity 1s linear;
}

#levelCounter,
#scoreCounter, #highCounter {
     	font-family: "Outfit", sans-serif;
	margin: 15px auto 0 auto;
	display: none;
	text-align: center;
}

.title {
	width: 150px;


}

.fades {
	animation: fadeinout 2s linear forwards infinite;
}

@-webkit-keyframes fadeinout {

	0%,
	100% {
		opacity: 0;
	}

	50% {
		opacity: 1;
	}
}

