[[module CSS]]
.footnotes-footer {
display: none;
}

:root {
    --white-bar: 255, 36, 60;
    --lg-bar: 255, 36, 60;
    --gray-bg: 12, 12, 12;
    --five-color: 255, 36, 60!important;
}


.interface {
    background: rgb(12, 12, 12);
    padding: 6px 6px;
    border-radius: 5px;
    border: 3px solid #ff243c;
}

.text-container .text {
  border-radius: 0;
  text-align: left;
  display:inline-block;
  padding:8px 12px;
  font-weight:500;
  margin:1.5px 10px;
  max-width: 300px;
}

.text-container-wrap {
    border: 3px solid #ff243c;
    background-color: rgb(12, 12, 12);
}

.text-container-wrap h1, .text-container-wrap h2, .text-container-wrap h3, .text-container-wrap h4, .text-container-wrap h5, .text-container-wrap h6 {
    color: #ff243c;
}

.text-container {
    border: 3px solid #ff243c;
    margin: 0px auto 25px;
    width: 325px;
    display: block;
    padding: 25px 0px 6px;
    background-color: rgb(12 12 12);
    box-shadow: none;
}

.sent .text {
  border-bottom-right-radius: 3px;
  background-color: rgb(29 29 29);
  color: rgb(226, 226, 226);
  border: 3px solid rgb(52, 52, 52);
}

.recv .text {
  border-bottom-left-radius: 3px;
  background-color: rgb(52, 52, 52);
  color: rgb(226, 226, 226);
  border: 3px solid rgb(122 122 122);
}

@media only screen and (max-width: 450px) {
.text-container {
   width: 300px;
}
}

.notation {
    border: solid 3px rgb(255, 36, 60);
    background: rgb(12 12 12);
    padding: 5px 25px 5px 25px;
}

.notation hr, .jotting hr, .darkdocument hr, .modal hr, .smallmodal hr, div.blockquote hr, .blockquote hr, blockquote hr {
    border-top: solid 2px #ff243c;
}

.top-right-box > .level {
    font-size: 2.3em;
}

.anom-bar > .bottom-box::before {
    position: absolute;
    content: " ";
    width: 100%;
    height: 8px;
    background-color: rgb(255, 36, 60);
    transform: translateY(-0.74rem);
}

.danger-diamond > .arrows {
    background-color: #07d6ff;
}

.modal {
    margin-top: 0.75rem;
    border: 3px solid rgb(12, 12, 12);
}

.smallmodal {
    border: solid 2px rgb(12 12 12);
    margin-top: 0.75rem;
}

.w3-grey, .w3-hover-grey:hover, .w3-gray, .w3-hover-gray:hover {
    color: #000;
    background-color: rgb(255, 36, 60);
}

.w3-border {
    border: 3px solid rgb(255, 36, 60);
    background-color: rgb(12 12 12);
}

#extra-div-1::before {
    font-size: 22.45px;
}

#extra-div-2::before {
    font-size: 1.20em;

}
[[/module]]

