[[module CSS]]
@import url('https://scp-wiki.wikidot.com/local--files/scp-7785/poorfish.css');
@import url('https://scp-wiki.wikidot.com/local--files/scp-7785/toscuchetcm.css');

#page-title {
  display: none;
}

#page-content .authorlink-wrapper {
  margin-top: 0.2rem;
}

.authorlink-wrapper > a {
  width:inherit !important;
  text-align:center !important;
  margin:auto;
 border-bottom:none !important;
}


#header h1 {
font-family: YRDZST;
color: #fff;
}

:root {
--header-subtitle: "控制，收容，保护";
--accent: 140, 69, 255;
--dark-bg-1: #211531;
--dark-bg-2: #0B0E15;
--head-font: YRDZST;
--logo-img: url(https://scp-wiki.wikidot.com/local--files/scp-7785/DrawnLogo.png);
}

.wideman {
  width: 100vw;
max-width: none;
  position: relative;
  left: calc(-50vw + 50%);
background: #fff;
color: black;
}

.wideman p {
   font-family: 'YRDZST';
   font-weight: normal;
   font-style: normal;
   font-size: 4em;
}

.pome p {
  font-family: 'YRDZST';
  font-weight: normal;
  font-style: normal;
  font-size: 2.5em;
  letter-spacing: -0.03em;
  line-height: 1.4em;
}

.frametop {
  margin-bottom:-7rem;
  display: block;
}

.frametop-mobile {
  width: 100vw;
  max-width: none;
  position: relative;
  left: calc(-50vw + 50%);
  margin-bottom:-5rem;
  display: none;
}

.bottomFrame {
margin-top:-5rem;
}

.fullWidthImg {
  width: 100vw;
  max-width: none;
  position: relative;
  left: calc(-50vw + 50%);
}

.imageDesktop { display:block; }
.imageMobile { display:none; }

@media only screen and (max-width: 640px) {

.pome p {
  font-size: 2.2em;
  letter-spacing: -0.04em;
}


.imageDesktop { display:none; }
.imageMobile { display:block; }

/* maybe not */
/* .frametop {display:none;} */

  .pome p {
    font-size: 2em;
  }

  .frametop {
    display: none;
  }

  .frametop-mobile {
    display: block;
  }
  .bottomFrame {
  width: 100vw;
  max-width: none;
  position: relative;
  left: calc(-50vw + 50%);
  margin-top: -2rem;
  }
}

[[/module]]
