[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Almendra+SC&display=swap');
@import url('https://fonts.googleapis.com/css2?family=New+Rocker&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Nova+Cut&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Martel&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Finlandica&display=swap');
:root {
  --header-title: "TACTICAL THEOLOGY";
  --header-subtitle: "INVENIENT NOVUM NUMEN";

--accentColor: hsl(41deg 100% 47%);
--accentColorLite: hsl(41deg 100% 57%);
--hue: 30deg;

--header-font: 'Finlandica', serif;
--body-font: 'verdana', sans-serif;
--textColor: #fdfdfd;
--textColorAlt: hsl(44.3, 0%, 100%);

--lgurl: url(https://scp-wiki.wdfiles.com/local--files/scp-6659/DoTT-White.png);
}


body {
    background-color: rgb(255 141 54);
    background-image: linear-gradient(180deg, #4A3933 0%, #4A393385 70%, #4A3933 100%), url(https://scp-wiki.wdfiles.com/local--files/theme%3Aad-abyssum/tacttheo_sidebar_small_compressed.png);
    background-attachment: fixed;
    background-size: cover;
    font-size: 0.81rem;
}

#header h1 a::before {
color: transparent;
background: linear-gradient(45deg, rgb(242, 220, 122) 0%, var(--accentColor) 50%, rgb(242, 220, 122) 100%);
-webkit-background-clip: text;
background-clip: text;
font-family: 'Nova Cut', sans-serif;
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
}

#header h2::before {
color: transparent;
background: linear-gradient(45deg, rgb(242, 220, 122) 0%, var(--accentColor) 50%, rgb(242, 220, 122) 100%);
-webkit-background-clip: text;
background-clip: text;
font-family: 'New Rocker', sans-serif;
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
letter-spacing: 5px !important;
margin-top: 6px;
}

#main-content h1, #main-content h2, #main-content h3, #main-content h4, #main-content h5, #main-content h6 {
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.6));
}

#header h2 {
  margin-top: 0.8em;
}

#header {
  height: 155px;
}

#extra-div-1 {
filter: opacity(.33) drop-shadow(0px 10px 3px #000);
}

#search-top-box-input, #search-top-box-input:hover, #search-top-box-input:focus, #search-top-box-form input[type="submit"], #search-top-box-form input[type="submit"]:hover, #search-top-box-form input[type="submit"]:focus {
font-size: unset;
}

#lock-info {
color: unset;
}

::selection {
color: var(--bgColor);
}

#top-bar {
top: 156px;
}

.top-bar::before {
background: none;
border-top: double 4px var(--accentColor);
top: -0.5rem;
}

.meta-title {
font-size: 238%;
margin: 0;
padding: 0;
}

.meta-title p {
  border-bottom: none;
}

#top-bar .open-menu a {
width: 1.15em;
height: 1.15em;
border-radius: 0.25em;
border: groove 2px var(--accentColor);
filter: drop-shadow(0px 6px 11px rgba(0,0,0,0.4));
}

.hovertip .f-content{
font-size: 0.81rem;
}

#page-content a.footnoteref {
    border: 0;
    font-size: .7rem;
    font-weight: bold;
}

@media (min-width: 768px) {
#page-content {
  font-size: inherit;
}

/* ---- SIDE-BAR ---- */
 #top-bar .open-menu a{
     background: #4A3933;
     border: thin solid var(--accentColor);
     border-radius: 0;
     box-shadow: none;
}
 #side-bar .heading{
     color: #4A3933;
     border-bottom-color: var(--textColor);

     font: bold 95% "Josefin Sans", sans-serif;
}
 #side-bar{
     padding: 1em 0;

     background: #4A3933;
}
 #side-bar .side-block{
     background: #4A3933;
     border: 0;
     box-shadow: none;
}
 #side-bar .side-block.media{
     background: #4A3933;
}
 #side-bar .side-block.resources{
     background: #4A3933;
}

#main-content .page-tags a {
    font: bold 0.75rem var(--body-font);
}

[[/module]]

