/* ==== EMAIL DIV ==== */
 .email-example .collapsible-block-folded :is(a.collapsible-block-link)  {
	animation: blink .8s ease-in-out infinite alternate;
}

 @keyframes blink {
	 0% {
		color: transparent;
		border-bottom-color: transparent;
	}

	 50%, 100% {
		color: var(--accentColor);
		border-bottom-color: var(--accentColor);
	}
}

 .email-example .collapsible-block-folded :is(a.collapsible-block-link:hover)  {
	color: var(--alphaColor) !important;
}

