	width: 88%;
	margin: auto;
	padding: .67rem 1rem .067rem;

	border: medium solid var(--keyColor);
	box-shadow: 0 1px 3px hsla(0, 0%, 0%, .5);
}

 .tofrom {
	padding: .067rem 1rem;

	border-left: thick solid hsl(359, 58%, 56%);
	background: var(--fgColor);
}
