[[module css]]
.page {
    display: block;
    overflow: hidden;
    font-family: "Monotype Corsiva", "Bradley Hand ITC", sans-serif;
    font-style: normal;

    background-attachment: scroll;
    background-clip: border-box;
    background-color: transparent;
    background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
    background-origin: padding-box;
    background-position: 0px 8px;
    background-repeat: repeat;
    background-size: 100% 20px;

    border: 1px solid #CCC;
    border-radius: 10px;
    padding: 10px 10px;
    margin-bottom: 10px;

    box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
    }
.page p,
.page ul {
    line-height: 20px;
    margin: 0;
}
[[/module]]
[[module CSS]]
sup {
vertical-align: top; position: relative; top: -0.5em;
}
:root{
--cutColour: #fafafa;
--barColour: #590505;
}
.translation_block{
display:none;}

.info-container .collapsible-block-folded a{
color: #FFF;}
.info-container .collapsible-block-unfolded-link a{
color: #FFF;}

.info-container .collapsible-block-link,
.info-container:hover .collapsible-block-link {
line-height: 1.6em;
}
.scp-image-block {
    box-shadow: initial;
    width: 50%;
}

.scp-image-caption {
    --wght: 600;
    font-size: 0.75rem;
    background-color: rgb(var(--light-gray-monochrome));
    padding: 0.25rem;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 100%;
    text-align: center;
    line-height: 1.25;
    color: rgb(var(--white-monochrome));
    font-weight: 600;
    letter-spacing: 0.0125rem;
    border-left: .0625rem solid rgb(var(--swatch-primary-darkest));
    border-right: .0625rem solid rgb(var(--swatch-primary-darkest));
    border-bottom: .0625rem solid rgb(var(--swatch-primary-darkest));
}

.scp-image-container, .scp-image-container * {
    box-sizing: border-box;
}

.document-main {
    position: relative;
}

.document-main > br {
    display: none;
}

.document-main > div {
    display: block;
    width: calc(100% - 3rem);
    background-color: rgb(var(--very-light-gray-monochrome), 1);
    padding: 0.75rem 0.75rem 0.5rem;
    margin: 1.5rem 0.5rem 0.5rem 0.5rem;
    border: 0.25rem rgb(var(--swatch-border-color)) solid;
    position: relative;
    line-height: 1.5;
    overflow-wrap: break-word;
    word-wrap: break-word;
    hyphens: auto;
    font-size: inherit;
}

.document-main > div > p:last-of-type {
    margin-bottom: 0;
}

.document-main > div::before {
    --wght: 600;
    content: " ";
    position: absolute;
    top: -1.25rem;
    left: -0.5rem;
    padding: 0.5em 0.5em;
    color: rgb(var(--white-monochrome));
    font-size: calc(12px + (14 - 12) * ( (100vw - 300px) / ( 800 - 300) ));
    font-family: var(--title-font);
    font-variant: all-petite-caps;
    font-weight: 600;
    letter-spacing: 0.05em;
    text-indent: 0;
    display: flex;
    line-height: 1;
    white-space: nowrap;
}

.document-main > div. sp-containment,
.document-main > div.discovery,
.document-main > div.description {
    background-color: rgba(var(--light-gray-monochrome),0.15);
    border-color: rgba(var(--swatch-primary-darkest),1);
}

.document-main > div.sp-containment::before,
.document-main > div.discovery::before,
.document-main > div.description::before {
    background-color: rgba(var(--swatch-primary-darkest),1)
}

.document-main > div.sp-containment::before {
    content: "Special Containment Procedures";    
}

.document-main > div.discovery::before {
    content: "Discovery";    
}

.document-main > div.description::before {
    content: "Description";
}

.document-main > .sp-containment > .sp-containment-image {
    grid-area: image;
    width: 15vw;
    float: right;
    padding-left: 1rem;
}

.document-main > .sp-containment > .sp-containment-image img {
    max-height: 110vh;
}

[[/module]]
