@keyframes strike{
0%   { width : 0; }
20% { width: 50%; }
40% { width: 75%; }
60% { width: 100%; }
80% { width: 50%; }
100% { width: 0; }
}
.strike, .unstrike {
  position: relative;
}
.strike::after, .unstrike::after {
  content: ' ';
  position: absolute;
  top: 50%;
  left: 0;
  width: 100%;
  height: 2px;
  background: rgb(253, 241, 242);
  animation-name: strike;
  animation-duration: 4s;
  animation-timing-function: linear;
  animation-iteration-count: infinite;
  animation-fill-mode: forwards;
}
@keyframes unstrike{
0%  { width: 0; }
20% { width: 25%; }
40% { width: 75%; }
60% { width: 50; }
80% { width: 100%; }
100% { width: 0; }
}
