[[include :scp-wiki:theme:basalt |hidetitle=a |centertitle=a]]
[[module CSS]]

/* CSS derived from the SCP-8000 Contest page, huge, massive, absolutely gargantuan thanks to syuzhet for their gorgeous design and incredible CSS/HTML skills that helped make this article what it is ❤️ */

#header h1 a::before {
    display: none;
}
#header h1 a span::after {
    margin-bottom: 0;
}
#container #header {
    background: none;
    position: absolute;
    box-shadow: unset;
    backdrop-filter: none;
}
div #container-wrap {
    background-color: #fdfdfd;
    background-attachment: fixed;
    background-size: cover;
}
#main-content {
    margin-top: 0;
}
#page-content {
    padding-top: 0;
}
#page-content > p:nth-child(-n+4) {
    display: none;
}
.widebox div {
    position: relative;
    top: 70%;
}
.widebox img {
    display: block;
    margin: auto;
    width: 35%;
}
.widebox #u-logotop {
    font-size: 2rem;
}
.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(100dvh);
    max-width: 100dvw !important;
    background-image: url(https://scp-wiki.wdfiles.com/local--files/scp-8018/Banner_Default.png);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: top;
}
.widebox:before {
    position: absolute;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    height: calc(100dvh);
    max-width: 100dvw !important;
    background-image: linear-gradient(180deg, transparent 0%, transparent 80vh, rgb(13,13,15) 100vh, transparent 100%);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    pointer-events: none;
}



@media only screen and (min-width: 1421px) {
/* Sidebox By EstrellaYoshte to the rescue! */

.limit {
     margin-bottom: -1rem;
    }
.anchor {
 position: -webkit-sticky;
 position: sticky;
 height: 0;
 top: 0;
 z-index: 9999;
 }
.sidebox {
  background-color: transparent;
/*  padding: .14rem 1rem .14rem;
  margin-top: 0;
  margin-bottom: 0.5rem;*/
  width: 500px;
  height: calc(100dvh);
  position: absolute;
  top: 0;
  left: calc(90dvh);
  z-index: 5;
  overflow: auto;
  box-sizing: border-box;
}
}

/* Blinking Memo for last page */
.memoblink .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.memoblink a.collapsible-block-link {font-weight: bold;
}


/*Email format stuff */
.email {float: center; border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px 10px 10px 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)
}
a.collapsible-block-link { font-weight: bold;
}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px maroon
}



/* Story Text Effects*/
.gregory {
  font-size: 1.5em;
  font-family: "Garamond", Times, serif;
}
.telepathy {
  font-size: 1.5em;
  text-shadow: 1px 1px lightgray;
  font-family: "Garamond", Times, serif;
}

/* Sidebar >:( */

#top-bar div.mobile-top-bar>.open-menu {
  visibility:visible;
  opacity: 0.6;
  transition: opacity 0.2s;
}
#top-bar div.mobile-top-bar>.open-menu:hover {
    opacity: 1;
}
#side-bar {
     margin-top:0;
     height:100%
}
#side-bar, #side-bar:is(:hover, :focus-within):not(:target) {
     left:calc(var(--side-bar-width) * -1)
}
#side-bar::after, #side-bar::before {
     display:none
}
#side-bar:target {
     left:0;
     width:var(--side-bar-width);
     z-index:10
}
#side-bar a.close-menu, #side-bar:target a.close-menu {
     display:block;
     pointer-events:none;
     position:fixed;
     top:0;
     right:0;
     left:auto;
     width:100%;
     min-width:3.75rem;
     height:100%;
     margin-left:0;
     opacity:0;
     z-index:-1;
     transition:width var(--side-bar-transition),opacity var(--side-bar-transition)   
}
#side-bar:target a.close-menu {
     width:calc(100% - var(--side-bar-width));
     pointer-events:auto;
     opacity:1;
}

.footnotes-footer {
  display:none;
}


.choicebox {
  background-color: transparent;
  width: 250px;


/* Mobile Compatibility */
@media only screen and (max-width: 1420px) {

#container #header {
  background: black;
  position: sticky;
}
.widebox div {
  position: relative;
  top: 60%
}
.widebox img {
  width: 100%
}
.widebox #u-logotop {
  font-size: 6vw;
}
.sidebox {
  background-color: transparent;
  padding: .14rem 1rem .14rem;
  margin-top: 0;
  margin-bottom: 0.5rem;
  width: 500px;
  height: calc(100dvh);
  position: absolute;
  top: 0;
  left: calc(20dvh);
  z-index: 5;
}
}
[[/module]]
[[div class="limit"]]
[[div class="anchor"]]
[[div class="sidebox"]]
