[[module CSS]]

/* Author Label Color */
.authorlink-wrapper > a {color:#634629;}

/* Rating Module Colors */
div.page-rate-widget-box .rate-points {background-color: #634629;border: solid 1px #634629;}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {background-color: #f5f5f5;border-top: solid 1px #473f63;border-bottom: solid 1px #634629;}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {background: transparent;color: #634629;}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {background: #634629;color: #f0f1f2;}
.page-rate-widget-box .cancel {background-color: #634629;border: solid 1px #634629}
.page-rate-widget-box .cancel a {background: transparent;color: #8c8279;}
.page-rate-widget-box .cancel a:hover {background: transparent;color: #f0f1f2;}

/* Rating Module In-line */
div.shift {
position:relative; top:3.2rem;
}

/* Logo In-line */
div.shift2 {
position:relative; top:-5rem;
}
@media (max-width: 479px) {div.shift2 {position:relative; top:-3rem;}}

/* The Clutter */
#page-title {display:none;}
.footnotes-footer {display:none;}
body {background-color:rgb(240, 240, 240)}

/* Top Bar */
div#header h1 span {font-size:0px}
div#header h1 a:before {content: "SCP Foundation"; color:#eee;}
div#header h2 span {font-size:0px; padding: 4px;}
div#header h2:after {
content: "Secure, Contain, Protect";
color:#f0f0c0;
font-family:Trebuchet MS;
font-size:110%;
}
div#container-wrap {background: url('http://scp-wiki.wikidot.com/local--files/scp-8044/Eternalbackground6.png') top left repeat-x;}
#header {background-image: url('https://scpwiki.github.io/sigma/images/header-logo.svg')}

/* Debrief Box */
div.cont {
font-family: 'Georgia';
font-size: 15px;
color: #303030;
border: dashed 1px #777;
background:#f9f9f9;
padding:0 2em;
text-align: left;
width:80%;
margin:auto;
box-shadow: 0 1px 1px rgba(0,0,0,.1);
}

div.dinkus {
text-align: center;
}

div.endlog {
font-family: 'Courier New';
font-size: 16px;
color: #000000;
}

[[/module]]

