[[module CSS]]
 :root{
   --header-title: "SCP Foundation";
   --header-subtitle: "Secure - Contain - Protect";
   --titleColor: #f2f2f2;
   --subtitleColor: #d9d952;
   --lgurl: url(https://scpwiki.github.io/sigma/images/header-logo.svg);
}
[[/module]]
[[include :scp-wiki:component:toggle-sidebar]]

[[module css]]

::-webkit-scrollbar-thumb {
  background-color: #afafaf;
}

body {
    background-color: #060606 !important;
    font-size: 0.90em;
    color: #363535;
}

#header h1 a {
    content: "SCP Foundation";
    text-shadow: 1px 1px 2px white, 0 0 1em red, 0 0 0.2em white;
    color: #000;
}

#header h1 a::before {
    content: "SCP Foundation";
}

#header h2::before {
    content: "Secure, Contain, Protect";
}

#header h1 a::before{
     text-shadow: 4px 4px 4px hsla(0, 0%, 0%, .8);
}
 #header h2::before{
     text-shadow: 3px 3px 3px hsla(0, 0%, 0%, .9);
}

 #header h1 a::before{
     font-family: BauhausLTDemi, 'Nanum Gothic', Arial, sans-serif;
}
 #header h2::before{
     font-family: 'Trebuchet MS', Arial, sans-serif;
}

 #header::before{
     background-size: auto 8.8em;
     margin-top: 0.5em;
     opacity: .46;
}

#header h2 {
    margin-left: 0;
    text-align: center;
    padding: 0;
    clear: center;
    float: none;
    font-size: 105%;
    max-height: 38px;
}
#header h2 span {
    display: block;
    margin: 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: " ";
    display: block;
    margin: 0;
    padding: 0px 0;
    line-height: 0px;
    max-height: 0px;
    font-weight: bold;
    color: #fff;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}

  .page-rate-widget-box {
  background-color: #200303;
  border: 2px solid #363535;
  color: #ededed;
  padding: .1em;
}

div.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
  background-color: transparent;   /*  clears sigma-9 colors  */
  border: 0;   /*  clears sigma-9 borders  */
  color: #ededed;
  text-transform: capitalize;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
  border-left: 2px solid #383737;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
  color: #ededed;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
  color: #363535;
}
.page-rate-widget-box .cancel {
  background-color: transparent;
  border: 0;
  border-left: 2px solid #383737;
}
.page-rate-widget-box .cancel a,
.page-rate-widget-box .cancel a:hover {
  border-radius: 0 5px 5px 0;
  color: #ededed;
}
.page-rate-widget-box .cancel a:hover {
  color: #363535;
}

.scp-image-block {
  border-color: #060606;
}

div.customblock{
color: #FFFFFF; float: center; border: solid 2px #FFFFFF; font-family: Courier New, Courier; font-size: 101%; width: auto; padding: 10px 25px; margin: 15px auto
}

.scp-image-block .scp-image-caption {
  background-color: #404040;
}

.dialogue {
  margin: 20px 0;
  padding-left: 4%;
  border-left: 3px solid #363535;
  padding-right: 4%;
}

.logtitle {
  font-size: 140%;
  text-align: center;
  color: #363535;
  font-weight: 900;
}

.fncon {
  font-family: var(--page-font) !important;
  border-color: rgb(var(--accent));
  background-color: #eee;
}

.fncon::before {
  background-color: rgb(var(--accent));
}

@media only screen and (min-width: 1278px) {
  .fncon {
    width: 14rem;
    border: none;
    border-left: 5px rgb(var(--accent)) solid;
  }

  .fncon::before {
    top: 14px; left: -10px;
  }
}

