[[module css]]
/* 汉化行距微调 */
body, li, p {
    line-height: 1.7;
}
@font-face {
  font-family: "OPPO Sans";
  src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/OPPOSansVF_3500.woff2') format("woff2-variations");
  /*font-weight: 300 700;*/
  font-display: swap;
}
@font-face {    
    font-family: "Punctuation";    
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
    unicode-range: U+2018-2019, U+201C-201D, U+2014, U+2026, U+00B7, U+FF1A-FF1B, U+FF01, U+FF0C, U+FF1F, U+3002;     
}
@font-face {    
    font-family: "Punctuation";    
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_Bold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
    unicode-range: U+2018-2019, U+201C-201D, U+2014, U+2026, U+00B7, U+FF1A-FF1B, U+FF01, U+FF0C, U+FF1F, U+3002;     
}
@font-face {
    font-family: 'ChillRoundF';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
    /* size-adjust: 111.76%; */
}
@font-face {
    font-family: 'ChillRoundF';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundF_Bold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
    /* size-adjust: 111.76%; */
}
@font-face {
    font-family: 'ChillRoundM';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/ChillRoundM_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
/* latin-ext */
@font-face {
  font-family: 'Martian Mono';
  font-style: normal;
  font-weight: 300;
  font-stretch: 100%;
  font-display: swap;
  size-adjust: 80%;
  src: url(https://fonts.gstatic.com/s/martianmono/v3/2V0PKIcADoYhV6w87xrTKjs4CYElh_VS9YA4TlTnaTS9wWmm.woff2) format('woff2');
  unicode-range: U+0100-02BA, U+02BD-02C5, U+02C7-02CC, U+02CE-02D7, U+02DD-02FF, U+0304, U+0308, U+0329, U+1D00-1DBF, U+1E00-1E9F, U+1EF2-1EFF, U+2020, U+20A0-20AB, U+20AD-20C0, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Martian Mono';
  font-style: normal;
  font-weight: 300;
  font-stretch: 100%;
  font-display: swap;
  size-adjust: 80%;
  src: url(https://fonts.gstatic.com/s/martianmono/v3/2V0PKIcADoYhV6w87xrTKjs4CYElh_VS9YA4TlTnaTq9wQ.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0304, U+0308, U+0329, U+2000-206F, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* latin-ext */
@font-face {
  font-family: 'Martian Mono';
  font-style: normal;
  font-weight: 700;
  font-stretch: 100%;
  font-display: swap;
  size-adjust: 80%;
  src: url(https://fonts.gstatic.com/s/martianmono/v3/2V0PKIcADoYhV6w87xrTKjs4CYElh_VS9YA4TlTnaTS9wWmm.woff2) format('woff2');
  unicode-range: U+0100-02BA, U+02BD-02C5, U+02C7-02CC, U+02CE-02D7, U+02DD-02FF, U+0304, U+0308, U+0329, U+1D00-1DBF, U+1E00-1E9F, U+1EF2-1EFF, U+2020, U+20A0-20AB, U+20AD-20C0, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Martian Mono';
  font-style: normal;
  font-weight: 700;
  font-stretch: 100%;
  font-display: swap;
  size-adjust: 80%;
  src: url(https://fonts.gstatic.com/s/martianmono/v3/2V0PKIcADoYhV6w87xrTKjs4CYElh_VS9YA4TlTnaTq9wQ.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+0304, U+0308, U+0329, U+2000-206F, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
/* 分级栏汉字字重 */
div.class1>div, div.item1>div {
    font-family: 'rift', 'ChillRoundM' !important;
    font-weight: 500 !important;
}
#skrollr-body {
  background-image: linear-gradient(to bottom, #0000 77.6%, #0003 90.6%, #000e 93.6%);
}
#footer {
    position: relative;
    background-color: transparent;
    color: rgb(var(--space-starlight));
    margin-top: 0;
    overflow: unset;
    isolation: isolate;
}
#footer::before {
    content: 0;
    position: absolute;
    bottom: 0; left: 0;
    height: 0; width: 100%;
    background-image: none;
    background-size: cover;
    background-position: center top;
    z-index: -1;
    pointer-events: none;
}
#page-title{display:none}
.footnotes-footer {
    visibility: hidden;
    height: 0;
    padding: 0;
    margin: 0;
}
/*header stuff*/
:root {
--sp_header-height: 250px;
--header-font: "Urbanist", "OPPO Sans", "Noto Sans SC", Nanum Gothic, sans-serif;
}

#extrac-div-1 { display: none; }
#header {
    grid-template-rows: auto 2fr auto auto 1fr auto;
}
#header::before {
    align-self: center;
    mix-blend-mode: normal;
}
[[/module]]

[[module CSS]]
/*theme coloring*/
:root {
    --space-sun: 69, 122, 191;
    --space-moon: 42, 105, 189;
}
[[/module]]

[[module CSS]]
/*blue border*/
.darksmol {
 border-style: solid none solid none;
 border-color: #FFFFFF;
 color: white;
 background-color: rgb(10,10,20);
 padding-top: 0;
 padding-right: 10px;
 padding-bottom: 0px;
 padding-left: 10px;
 border-width: 2px;
 font-family: "Punctuation", 'Martian Mono', 'ChillRoundF', monospace;
}
/*grey border*/
.darksmyl {
 border-style: solid;
 border-color: rgb(156, 146, 147);
 color: white;
 background-color: #000000cc;
 padding-top: 0;
 padding-right: 10px;
 padding-bottom: 10px;
 padding-left: 10px;
 border-width: 2px;
 font-family: "Punctuation", 'Martian Mono', 'ChillRoundF', monospace;
}
.darksmal {
 filter: invert(1) hue-rotate(260deg);
}
[[/module]]

