:root {
    --ACS-font: 'Poppins', 'GlowSansSCExt', sans-serif;
    --body-font-primary: 'Punctuation', 'Zen Latin', 'GlowSansSC';
    --swatch-menutxt-dark-color: 0, 0, 0;
    --swatch-menubg-black-color: 8, 26, 23;
    --scrollbar-track-color: 0,0,0;
    --mono-font-fallback: 'Sometype Mono', monospace;
    --general-border-color: 93, 127, 127;
    --admo-color-bright: 0, 77, 46;
    --admo-color-alt: 133, 144, 140;
    --basalt-main-text-color: 255, 255, 255;
    --admo-color-bright: 114, 164, 136;
    --admo-color-alt: 133, 144, 140;
    --header-font-primary: 'Poppins', 'GlowSansSCExt', sans-serif;
    --mono-font-primary: 'JetBrains Mono', 'ChillRoundF', monospace;
}
.zhuanming, .zm {
    display: inline-block;
    text-decoration: none;
    border: 0; /* 解决 Safari 浏览器的一个 bug */
    border-bottom: 1px solid;
    border-image: linear-gradient(currentcolor, currentcolor) 1 0 / 1 0.1em;
}
.phigher {
    line-height: 1.8;
}

.centerer {
display: flex;
justify-content: center;
margin-top:10px;
margin-bottom:10px;
}
.centerer .wiki-content-table {
width:100%;
margin: 0 auto;

}
.foldable-list-container a, .grn a {color:#72a488 !important;font-family: "Poppins", 'GlowSansSCExt', sans-serif;font-weight: 500;}

div.convo hr {background-color: rgb(163, 163, 163); margin-block: 0rem; margin-inline: 0rem; height: 0.1rem;}

.conds {font-family: 'Poppins', 'GlowSansSCExt', var(--header-font);}

.cond {
    font-family: 'Roboto Condensed', 'GlowSansSCCond', var(--UI-font-fallback);
}

body {
background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 1) 100%), url("https://scp-wiki.wdfiles.com/local--files/scp-8190/rcr.png");
background-repeat:no-repeat;
background-size:cover;
background-attachment: scroll, fixed;
color:F5F5F5;
}



/* tables and tickets and wrappers thereof */

:is(#page-content table:not(.form), table.wiki-content-table) th {color: #120e0e;}

.dept {padding:9px;width:90%;margin:0 auto;background-color:#151616;border-radius:13px;}

.dept table.wiki-content-table td, .clearance table.wiki-content-table td {padding:5px!important;}

div.tableb :is(#page-content table:not(.form), table.wiki-content-table) {border-collapse: separate;border-spacing: 4px;}

.convo hr {background-color: #d3d3d3;}

:where(#page-content) table:not(.form), table.wiki-content-table {margin: 0px;border:solid .01rem rgb(34,35,34);}

:is(#page-content table:not(.form), table.wiki-content-table) td {background-color:rgba(240,246,240,0.1) !important;padding: 2px;}

/* ACS element styles */

.acs-extra .class-text {font-size: calc(8px + (12 - 8) * ((100vw - 400px) / (800 - 400))) !important;font-weight: 700 !important;}

.acs-extra {background-color: rgba(12,255,175,.5);margin-left: 0px;}

.acs-extra-1,.acs-extra-2,.acs-extra-3 {border-left:none !important;}

.bottom-icon::before, top-icon::before {background-color: #f0f6f0 !important;}

.acs-extra-1 > .class-category, .acs-extra-2 > .class-category, .acs-extra-3 > .class-category, .acs-extra-4 > .class-category {font-size: 1.4rem !important;}

.icon-1 {background-size: -2.5rem 0.5rem !important;background-position: center !important;}

.acs-extra-1 , .acs-extra-1 div {font-size:125% !important;}

.anom-bar > div.bottom-box::before { background-color: transparent !important; }
.text-part .disrupt-class::before, .text-part .risk-class::before { background-color: transparent !important; }
.bottom-box > div.diamond-part::before {background-color: transparent !important; }


/* footnotes */

.footnotes-footer {display:none;}
.fnnum:after {color:#19a774;font-family: "Poppins", 'GlowSansSCExt', sans-serif;font-weight: 500;}




/* intermission-specific admo restyles */

.modal {
border-width: 0.15rem !important;
padding: 20px !important;
}

.admo-intermission_splash .ctrl { font-size: min(6rem, 6vw); letter-spacing: min(.4rem, .4vw);}

.floatbox {display:inline-block;padding:8px 12px;font-weight:500;margin:1.5px 10px;max-width: 300px;margin: 10px;}

div.ettra_memo {--memo-color:134, 173, 169;background-size: 90% auto !important;background-position: center center !important;background-color: rgb(0, 13, 24) !important;font-size: 1.1em;}

.bt a:any-link {color:black !important;}

div:is(.jotting) {border: 2px solid black;padding-inline: 2.5rem;}

