/* modified default/ACS element styles */

.collapsible-block {box-shadow: none !important;}

/* mobile styling */

@media screen and (max-width: 768px) {
.document{font-size: 50%;max-height: 100dvh;max-width:50%;}
.level {font-size: 2.3em !important;}
.poppin {padding: 0px 6px 6px 6px;}
}

@media only screen and (max-width: 768px) {
  #page-content .convo{
    margin-left: unset;
    margin-right: unset;
  }
  img {
    float: none;
  }
}



/* icon animations */

@keyframes flip-with-rotate {
    0%{transform: perspective(400px) rotateY(0);}
    100%{transform: perspective(400px) rotateY(180deg);}
}

@keyframes rotation {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(359deg);
  }
}


.broken img {
will-change:transform;
animation-name: flip-with-rotate;
animation-duration: 4s;
animation-iteration-count: infinite;
margin: 0 10px;
vertical-align: middle;
}

.asterisk {
will-change:transform;
/* animation: rotation 8s infinite linear; */
width:80px;
}

