}

/* SIDE-BAR */
 #top-bar .open-menu a{
     color: hsl(33, 100%, 69%);
     background: hsl(214, 48%, 12%);
     border-color: hsl(33, 100%, 69%);
}

/* INFO BAR */
 .info-container{
