}

/* ---- BETTERFOOTNOTES ---- */
 .fnnum{
     font-size: .95em;
}
 .fncon{
     background: hsl(215, 48%, 20%);
     border-color: hsl(33, 100%, 69%);
}
 .fncon::before{
