[[module css]]
/* Logo Customization */
:root {
    --proposal-logo: url(https://scp-wiki.wdfiles.com/local--files/theme%3Apenumbra/logo.png);
    --proposal-logo-filter: invert(100%);
    --proposal-logo-size: 15rem;
}

.proposal-logo {
    min-height: 15rem;
    filter: var(--proposal-logo-filter);
    background: var(--proposal-logo);
    background-size: var(--proposal-logo-size);
    background-repeat: no-repeat;
    background-position: center center;
}

.proposal-title {
    font-size: 2em;
}

.proposal-subtitle {
    font-size: 1.5em;
}

.proposal-title,
.proposal-subtitle {
    position: relative;
    margin: .5rem 1.25rem;
    margin-bottom: 0;
    padding: .5rem 1.25rem;
    padding-bottom: 0;
    --wght: 700;
    font-family: var(--title-font);
    text-align: center;
    color: rgb(var(--gray-monochrome));
}

.proposal-title p,
.proposal-subtitle p {
    margin: 0;
}

.proposal-author {
font-style: italic;
text-align: right;
}

/* Document Base */
.document {
    display: block;
    position: relative;
    max-width: 45rem;
    margin: 1.25em 1.25em 2.25em 1.25em;
    padding: 1.25em;
    color: rgb(var(--gray-monochrome));
    background-color: rgb(var(--white-monochrome));
    box-shadow: 0 0 10px rgba(var(--dark-gray-monochrome), .5);
}

.document::before {
    top: 5px;
    left: -5px;
    transform: rotate(-2.5deg);
}

.document::after {
    top: 2.5px;
    right: -2.5px;
    transform: rotate(1.25deg);
}

.document::before,
.document::after {
    content: "";
    position: absolute;
    z-index: -1;
    height: 98%;
    width: 98%;
    background-color: rgb(var(--white-monochrome));
    box-shadow: 0 0 10px rgba(var(--dark-gray-monochrome), .5);
}

@media only screen and (max-width: 1020px) {
.document::before,
.document::after {
        display: none;
    }
}

    /* Fix for BetterFootnotes overlapping sidebox by T3DK */
    .anchor {
     z-index: 8;
    }
[[/module]]

