=====
[[include :scp-wiki:theme:blankstyle]]
[[include :scp-wiki:theme:darkqueer]]

[[include :scp-wiki:component:dynamic-emails code=--]]

[[module css]]
@import url('https://scp-wiki.wikidot.com/local--files/scp-8980/departuremono.css');

:root {
  --fade-in: none;
}

.fncon {
    background:green;
    right: -5em !important;
}

.header-box > .top-right-box {
    filter: invert(1);
}

.footer-box > .bottom-left-box {
    border-left: 2px solid var(--off-white);
}

/* ACCESSIBILITY */

.accessibilityToggle {
    text-align: center;
    display: inline-block;
    height: 100%;
    width: 50%;
    padding: 0 0.2em;
    background: var(--accent-color);
    border: 2px solid var(--lighter-color);
}

.cbMode {
    font-family: 'Departure Mono';
    padding: 0.5em;
    background: var(--darker-color);
    border: 3px solid var(--accent-color);
    width: 45%;
    font-size: 95%;
    margin: 0em 0.4em 1em 0.5em;
    text-align: left;
    display: inline-flex;
    justify-content: space-between;
    align-items: center;
}

.cbMode .collapsible-block {
    display:inline-block;
}

.cbMode .collapsible-block .collapsible-block-folded a {
    color: var(--lighter-color) !important;
    font-size: 120%;
}

.cbMode .collapsible-block .collapsible-block-unfolded a {
    color: initial;
    font-size: 120%;
}

.cbMode:has(.collapsible-block-unfolded[style="display: none;"]), .cbMode:has(.collapsible-block-unfolded[style="display:none"]) {
    border: 2px solid var(--lighter-color) !important;
    filter: contrast(0.7);
}

.cbMode:has(.collapsible-block-unfolded[style="display: none;"]) .accessibilityToggle, .cbMode:has(.collapsible-block-unfolded[style="display:none"]) .accessibilityToggle {
    background: var(--darker-color);
}

.srMode {
    font-family: 'Departure Mono';
    padding: 0.5em 1em;
    background: var(--darker-color);
    border: 3px solid var(--accent-color);
    width: 45%;
    font-size: 95%;
    margin: 0em 0em 1em 0em;
    text-align: left;
    display:inline-flex;
    justify-content: space-between;
    align-items: center;
}

.srMode .collapsible-block {
    display:inline-block;
}

.srMode .collapsible-block .collapsible-block-folded a {
    color: var(--lighter-color) !important;
    font-size: 120%;
}

.srMode .collapsible-block .collapsible-block-unfolded a {
    color: initial;
    font-size: 120%;
}

.srMode:has(.collapsible-block-unfolded[style="display: none;"]), .srMode:has(.collapsible-block-unfolded[style="display:none"]) {
    border: 2px solid var(--lighter-color) !important;
    filter: contrast(0.7);
}

.srMode:has(.collapsible-block-unfolded[style="display: none;"]) .accessibilityToggle, .srMode:has(.collapsible-block-unfolded[style="display:none"]) .accessibilityToggle {
    background: var(--darker-color);
}

.srPre {

}

@media (max-width: 750px) {
    .cbMode, .srMode {
        width: 80%;
        margin: 0em auto 1em auto !important;
        display:flex;
}
}

.randMod:has(.srMode .collapsible-block-unfolded[style="display: none;"]) .srPre, .randMod:has(.srMode .collapsible-block-unfolded[style="display:none"]) .srPre {
    display: none !important;
}

.randMod:has(.cbMode .collapsible-block-unfolded[style="display: none;"]) :is(.ontoTip.ontoProb, .ontoTip.ontoUncertain, .ontoTip.ontoImprob, .ontoTip.ontoRand)::after, .randMod:has(.cbMode .collapsible-block-unfolded[style="display:none"]) :is(.ontoTip.ontoProb, .ontoTip.ontoUncertain, .ontoTip.ontoImprob, .ontoTip.ontoRand)::after {
    display: none !important;
}

.randMod:has(.cbMode .collapsible-block-unfolded[style="display: none;"]) :is(.ontoTip.ontoProb, .ontoTip.ontoUncertain, .ontoTip.ontoImprob, .ontoTip.ontoRand), .randMod:has(.cbMode .collapsible-block-unfolded[style="display:none"]) :is(.ontoTip.ontoProb, .ontoTip.ontoUncertain, .ontoTip.ontoImprob, .ontoTip.ontoRand) {
    outline: 2.5px solid transparent !important;
    text-decoration: underline 2px transparent !important;
}

.ontoTip {
    position: relative;
}

.ontoTip.ontoProb {
    background: none;
    outline: 2.5px solid transparent;
    color: cyan;
    text-decoration: underline 1px solid cyan;
}

.ontoTip.ontoProb:hover {
    outline: 2.5px solid cyan;
    z-index: 1;
}

.ontoTip.ontoProb::after {
    visibility: hidden;
}

.ontoTip.ontoProb:hover::after {
    text-align: center;
    content:"PROBABLE";
    position: absolute;
    font-size: 1.3em;
    background: var(--darker-color);
    border: 2px solid cyan;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    padding: 0 0.3em;
    visibility: visible;
}

.ontoTip.ontoUncertain {
    background: none;
    outline: 2.5px solid transparent;
    color: #faf279;
    text-decoration: underline 2px dashed #faf279;
}

.ontoTip.ontoUncertain:hover {
    outline: 2.5px solid #faf279;
    z-index: 1;
}

.ontoTip.ontoUncertain::after {
    visibility: hidden;
}

.ontoTip.ontoUncertain:hover::after {
    text-align: center;
    content:"UNCERTAIN";
    position: absolute;
    font-size: 1.3em;
    background: var(--darker-color);
    border: 2px solid #faf279;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    padding: 0 0.3em;
    visibility: visible;
}

.ontoTip.ontoImprob {
    background: none;
    outline: 2.5px solid transparent;
    color: salmon;
    text-decoration: underline 3px dotted salmon;
}

.ontoTip.ontoImprob:hover {
    outline: 2.5px solid salmon;
    z-index: 1;
}

.ontoTip.ontoImprob::after {
    visibility: hidden;
}

.ontoTip.ontoImprob:hover::after {
    text-align: center;
    content:"IMPROBABLE";
    position: absolute;
    font-size: 1.3em;
    background: var(--darker-color);
    border: 2px solid salmon;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    padding: 0 0.3em;
    visibility: visible;
}

.ontoTip.ontoRand {
    background: none;
    outline: 2.5px solid transparent;
    color: violet;
    text-decoration: underline 1px wavy violet;
}

.ontoTip.ontoRand:hover {
    outline: 2.5px solid violet;
    z-index: 1;
}

.ontoTip.ontoRand::after {
    visibility: hidden;
}

.ontoTip.ontoRand:hover::after {
    text-align: center;
    content:"UNPREDICTABLE";
    position: absolute;
    font-size: 1.3em;
    background: var(--darker-color);
    border: 2px solid violet;
    top: 100%;
    left: 50%;
    transform: translateX(-50%);
    padding: 0 0.3em;
    visibility: visible;
}

.ontoTip.span:is(A,B,C,D,E,F,G,H,I,J) {
    font-size: 15em !important;
}

.dyna-email {
    background-color: var(--darkest-color);
}

/* SYST PRINTOUT UI */
.syst {
    width: 100%;
    box-sizing: border-box;
    background-color: #1B111B;
    border: 5px solid var(--lighter-color);
    font-family: 'Departure Mono';
    padding: 10px;
    font-size: 90%;
}

.systInner {
    background-color: rgba(25, 25, 25, 0.6);
    border: 1px solid black;
    padding: 0.5em 1.5em;
}

.systTitle {
    font-family: 'Departure Mono';
    font-size: 2rem;
    padding: 0;
    margin: auto;
}

:is(.syst) > p {
    margin: auto;
}

@media (max-width: 733px) {
    .systTitle {
        font-size: 7vw;
    }
}

.forSale{
    color: transparent;
    cursor: url(https://scp-wiki.wdfiles.com/local--files/scp-8426/pointer_edit.png), default;    
}

/* Randomized Information */

.randMod {
    padding-block-end: .001rem;
}


.randMod div[id] { display: none; }

div:is(.imgH, .imgI, .imgJ) {
    display: none;
}

span:is(.A, .B, .C, .D, .E, .F, .G, .H, .I, .J) {
    color: violet;
    display: none;
}

.end td span:is(.A, .B, .C, .D, .E, .F, .G, .H, .I, .J) {
    color: inherit;
}

.randMod div:is([id$='0']) ~ p span.A, .randMod div:is([id$='0']) ~ .blockquote table.wiki-content-table tbody tr td span.A, .randMod div:is([id$='0']) ~.blockquote span.A, .randMod div:is([id$='0']) ~ .end table.wiki-content-table tbody tr td span.A {
    display: inline;
}

.randMod div:is([id$='1']) ~ p span.B, .randMod div:is([id$='1']) ~ .blockquote table.wiki-content-table tbody tr td span.B, .randMod div:is([id$='1']) ~.blockquote span.B, .randMod div:is([id$='1']) ~ .end table.wiki-content-table tbody tr td span.B {
    display: inline;
}

.randMod div:is([id$='2']) ~ p span.C, .randMod div:is([id$='2']) ~ .blockquote table.wiki-content-table tbody tr td span.C, .randMod div:is([id$='2']) ~.blockquote span.C, .randMod div:is([id$='2']) ~ .end table.wiki-content-table tbody tr td span.C {
    display: inline;
}

.randMod div:is([id$='3']) ~ p span.D, .randMod div:is([id$='3']) ~ .blockquote table.wiki-content-table tbody tr td span.D, .randMod div:is([id$='3']) ~.blockquote span.D, .randMod div:is([id$='3']) ~ .end table.wiki-content-table tbody tr td span.D {
    display: inline;
}

.randMod div:is([id$='4']) ~ p span.E, .randMod div:is([id$='4']) ~ .blockquote table.wiki-content-table tbody tr td span.E, .randMod div:is([id$='4']) ~.blockquote span.E, .randMod div:is([id$='4']) ~ .end table.wiki-content-table tbody tr td span.E {
    display: inline;
}

.randMod div:is([id$='5']) ~ p span.F, .randMod div:is([id$='5']) ~ .blockquote table.wiki-content-table tbody tr td span.F, .randMod div:is([id$='5']) ~.blockquote span.F, .randMod div:is([id$='5']) ~ .end table.wiki-content-table tbody tr td span.F  {
    display: inline;
}

.randMod div:is([id$='6']) ~ p span.G, .randMod div:is([id$='6']) ~ .blockquote table.wiki-content-table tbody tr td span.G, .randMod div:is([id$='6']) ~.blockquote span.G, .randMod div:is([id$='6']) ~ .end table.wiki-content-table tbody tr td span.G {
    display: inline;
}

.randMod div:is([id$='7']) ~ p span.H, .randMod div:is([id$='7']) ~ .blockquote table.wiki-content-table tbody tr td span.H, .randMod div:is([id$='7']) ~.blockquote span.H, .randMod div:is([id$='7']) ~ .end table.wiki-content-table tbody tr td span.H {
    display: inline;
}

.randMod div:is([id$='8']) ~ p span.I, .randMod div:is([id$='8']) ~ .blockquote table.wiki-content-table tbody tr td span.I, .randMod div:is([id$='8']) ~ .blockquote span.I, .randMod div:is([id$='8']) ~ .end table.wiki-content-table tbody tr td span.I {
    display: inline;
}

.randMod div:is([id$='9']) ~ p span.J, .randMod div:is([id$='9']) ~ .blockquote table.wiki-content-table tbody tr td span.J, .randMod div:is([id$='9']) ~.blockquote span.J, .randMod div:is([id$='9']) ~ .end table.wiki-content-table tbody tr td span.J {
    display: inline;
}

.randMod div:is([id$='7']) ~ div.imgH {
    display: block;
}

.randMod div:is([id$='8']) ~ div.imgI {
    display: block;
}

.randMod div:is([id$='9']) ~ div.imgJ {
    display: block;
}

.queerbox_large {
    border-radius: 20px;
    margin: 0.5em auto 1em auto;
    width:95%;
}

@media (min-width: 450px) and (max-width: 750px) {
    .wiki-content-table {
        font-size:90%;
}
}

@media (max-width: 449px) {
    .wiki-content-table {
        font-size: 80%;    
}
}

@media only screen and (max-width: 600px) {
    .scp-image-block.block-right {
