        width: 40% !important;
}
}

@media only screen and (min-width: 600px) {
    .post {
        display: none !important;
}
}

/* Info for Accessibility Modes */

#page-content .randMod .creditRate {
    margin-right: -2em;
    margin-top: 1em;
    font-size: 1.5em;
}

.randMod .creditButtonStandalone p a {
    background: var(--off-white);
    box-shadow: 0 0 3px var(--accent-color);
    width: 1.4em;
    height: 1.4em;
}

.randMod .creditButtonStandalone p a:hover {
    background: var(--accent-color);
}

.randMod .modalbox {
    background: var(--darkest-color);
    border: solid 2px var(--brighter-white);
}

#page-content .randMod .modalbox a {
    color: var(--off-white);
}

#page-content .modalcontainer {
