}

#page-content .modalbox hr {
    margin: 0.5em 2em;
}

#page-content .modalbox .credit {
    height: 40vh;
    font-size: 0.8em;
}

@media only screen and (max-width: 800px) {

#page-content .randMod .creditRate {
    display: flex;
    margin: 0em auto 10px auto;
    justify-content: center;
    float: none;
}

}

