[[module CSS]]
:root {
	--timeDelay: 0.5s;
}

a.collapsible-block-link:not(.licensebox a.collapsible-block-link) {
	font-weight: 600;
	color: white;
	padding-top: 4px;
	padding-bottom: 4px;
	padding-left: 7px;
	padding-right: 9px;
	background: rgb(20, 20, 20);
	border-radius: 6px;
	margin-top: 10px;
	margin-bottom: 10px;
}

#page-content .normal a {
   color: #000000;
   background-color: initial;
   filter: initial;
   transition: background-color 0.15s linear;
}
#page-content .normal a:hover {
	color: #EE4B2B;
	background-color: var(--accentColor);
}

@import url('https://fonts.googleapis.com/css?family=Pangolin&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Covered+By+Your+Grace&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Gugi&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Schoolbell&display=swap');

:root {

}
.ad_memo>p:nth-child(1)::before {
	content: "DEPARTMENT OF POLITICAL SCIENCE REPORT\00000a";
	font-family: 'Work Sans', sans-serif;
	font-weight: 900;
	font-size: 130%;
	text-align: center;
	margin-bottom: 1rem;
	padding-bottom: 0.3rem;
	border-bottom: dotted 0.13rem darkslategrey;
	max-width: 49rem;
	margin-left: auto;
	margin-right: auto;
	display: flex;
	flex-direction: column;
	align-content: stretch;
	align-items: center;
}
.ad_memo {
	background: url(https://scp-wiki.wdfiles.com/local--files/scp-7000/ADtransparent.png) #F4DFDF;
	background-size: 400px;
	background-position: center;
	background-repeat: no-repeat;
	box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
	width: 90%;
	min-height: 4rem;
	margin-left: auto;
	margin-right: auto;
	padding: 10px 20px 20px 20px;
	word-break: break-word;
}
.overwatch_memo {
	background: #EBEBEB;
	background-size: contain;
	background-position: center;
	background-repeat: no-repeat;
	box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.5);
	width: 90%;
	margin-left: auto;
	margin-right: auto;
	padding: 10px 30px 20px 20px;
	word-break: break-word;
}
.classic {
	display: block;
	font-family: 'Pangolin';
	font-size: 1.3em;
	background-attachment: scroll;
	background-clip: border-box;
	background-color: transparent;
	background-image: linear-gradient(to top ,rgb(202, 219, 228) 0%, rgb(231, 233, 220) 8%);
	background-origin: padding-box;
	background-position: 0px 8px;
	background-repeat: repeat;
	background-size: 100% 20px;
	border: 1px solid #CCC;
	border-radius: 10px;
	padding: 10px 10px;
	margin-bottom: 10px;
	box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2)
	}
.classic ul {
	list-style: symbols;
}

.cyberpunk {
	background-color: #212121;
	border-top: 0.2rem solid red;
	border-bottom: 0.2rem solid red;
	border-radius: 5% 13%;
	color: #FF3300;
	padding: 0.5rem 1rem;
	margin: 1em auto;
	width: auto;
	font-family: 'Gugi', cursive;
	font-size: 1em;
}

.kiddo {
	background-image: linear-gradient(to top ,rgb(177, 156, 217) 0%, rgb(255, 209, 220) 8%);
	font-family: 'Schoolbell', cursive;
	font-size: 1.1em;
	border-radius: 2%;

}

.wartime {
   background: url(http://scptestwiki.wdfiles.com/local--files/conyurt3/paper.png);
   font-family: 'Covered By Your Grace', cursive;
   box-shadow: 0px 1px 3px rgba(0, 0, 0, 0.2);
   font-size: 1.2rem;
   padding: 0.6rem;
   margin: 0.7em auto;
}

.nobody {
	width: 70%;
	margin: 1rem auto;
	text-align: center;
	padding: 0.6rem;
	font-size: 1rem;
}
