    background: linear-gradient(rgb(18, 16, 16, 0.01) 50%, rgb(0, 0, 0, 0.25) 50%), linear-gradient(90deg, rgb(255, 0, 0, 0.1), rgb(0, 255, 0, 0.06), rgb(0, 0, 255, 0.1));
