[[module CSS]]
@font-face {
    font-family: 'LXGWWenKai';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/LXGWWenKai_Regular_3500.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'LXGWWenKai';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/LXGWWenKai_Bold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
}
@font-face {
    font-family: 'Honor Sans';
    src: url('https://cdn.jsdelivr.net/gh/WinterholdPrime/CDN@main/HONORSansCN_Bold_3500.woff2') format('woff2');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
}

:root {
    --desktop-wallpaper: url(http://scpdsandbox.wikidot.com/local--files/gaia/earthrise.jpg);
    --window-radius: 0.95rem;
    --header-font-primary: "Red Hat Display", "Honor Sans", "Noto Sans SC", sans-serif;
}

#page-title,#breadcrumbs { display: none; }
#page-content {
    padding: 0;
    background: transparent;
    border-radius: 0;
}

div.rate-box-with-credit-button .creditButton>p>a::before {
    border-radius: calc(var(--window-radius)/4);
}

/*--------------------------------------------------*/

.head-container {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-wrap: wrap;
    background-color: rgb(var(--basalt-secondary-color));
    padding: .15rem .875rem;
    gap: .25rem .75rem;
    border-start-start-radius: var(--window-radius);
    border-start-end-radius: var(--window-radius);
}

.head-container .pseudo-title {
    display: grid;
    flex-grow: 1;
    font-family: var(--header-font);
}
.pseudo-title span:first-child {
    display: inline-block;
    padding-block: 0.125rem;
    font-size: 0.675rem;
}
.pseudo-title span:first-child a {
    display: inline-block;
    background-color: rgb(var(--basalt-background-color));
    border-radius: var(--window-radius);
    padding: 0.125em 0.5em;
    transition: background-color 0.125s ease-out;
}
.pseudo-title span:first-child a:is(:hover,:focus) {
    text-decoration: none;
    background-color: rgb(var(--link-color));
    color: rgb(var(--basalt-secondary-color));
}

.pseudo-title span:nth-child(2) {
    font-weight: bold;
    font-size: 1.325rem;
}

.head-container .rate-box-with-credit-button {
    margin: 0;
    box-shadow: none;
}

ul.creditRate + div { display: none; }

.body-container {
    padding: .9125rem;
    position: relative;
    border-end-start-radius: var(--window-radius);
    border-end-end-radius: var(--window-radius);
}
.body-container::before {
    content: "";
    position: absolute;
    top: 0; left: 0;
    width: 100%; height: 100%;
    box-shadow: var(--window-shadow);
    background-color: rgb(var(--basalt-background-color),var(--basalt-UI-opacity));
    -webkit-backdrop-filter: blur(.175rem);
    backdrop-filter: blur(.175rem);
    border-radius:  inherit;
    z-index: -1;
}

.danger-diamond > .quadrants > div {
    background-color: transparent !important;
}

.skip_box.terminal::after{
    content: attr(data-title);
    font-size: min(calc(1.5vw + .25em),1em);
    line-height: 1.25;
}

/*--------------------------------------------------*/

.body-container h3 {
    margin-block-start: 1.5rem;
    border-block-end: 0.05rem solid rgb(var(--basalt-main-text-color),0.25);
    padding-block-end: 0.325rem;
}

