[[module CSS]]
/*

    Dreamy Night Theme
    [2024 Wikidot Theme]
    Created by DianaBerry and Queerious
    Created by modifying the Just Girly Things Theme by stormbreath and Blood and Gutz them by Mew-ltiverse and Angryman22

/* --------- EMAIL CSS ----------- */
.email-example .collapsible-block-folded a.collapsible-block-link {
    animation: blink 0.8s ease-in-out infinite alternate;
}
@keyframes blink {
    0% { color: transparent; }
    50%, 100% { color: #b01; }
}
.email {border: solid 2px #000000; width: 88%; padding: 1px 15px; margin: 10px; box-shadow: 0 1px 3px rgba(0,0,0,.5)}
.email-example a.collapsible-block-link {font-weight: bold;}
.tofrom {margin-left: 10px; margin-top: 5px; padding: 1px 15px; border-left: solid 3px hsl(268, 100%, 62%)}

/* -------------- HEADER -------------- */
div#container-wrap {
    background-image: none;
}

div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background: url("http://scp-sandbox-3.wikidot.com/local--files/collab:diana-tests-things/dreamyheader2");
    background-size: cover;
    background-position: 20% 20%;
}

div#extra-div-2 {
    height: 22px;
    width: 100%;
    top: 140px;
    position: absolute;
    background: linear-gradient(to bottom,
      hsl(260, 100%, 75%),
      hsl(260, 100%, 75%));
}

h1 {
    color: #8A6CCD;
}
div#header h1 a span {
    font-size: 0px;
}
div#header h1 a:before {
    content: "Dreamy Nights";
      font-weight: bold;
    color: #ac82ff;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}

div#header h2 span {
    font-size:0px;
    padding: 4px;
}
div#header h2:after {
    content: "Can I escape?";
    font-weight: bold;
    color: #ac82ff;
    padding: 19px 0;
    text-shadow: 1px 1px 1px rgba(0, 0, 0, .8);
    white-space: pre;
}


#header {
    background-image: url('http://scp-sandbox-3.wikidot.com/local--files/collab:diana-tests-things/Dreamylogo2');
    background-size: 6rem;
}

/*-------------- LOG-IN INFO -------------- */
#login-status,
#login-status a {
    color: hsl(231, 100%, 71%);
}

#login-status ul a:hover {
   font-weight: bold;
}

#account-topbutton {
    background: transparent;
    color: hsl(335, 100%, 14%);
    border: none;
}

#account-options {
    background: hsl(335, 90%, 95%);
    border-color: hsl(335, 55%, 55%);
}

/* -------------- SEARCH-BAR -------------- */
#search-top-box-input {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(252, 83%, 70%);
}

#search-top-box-input:hover,
#search-top-box-input:focus {
     background-color: hsl(231, 100%, 71%);
}

#search-top-box-form input[type=submit] {
     border: solid 1px #e5e5e5;
     color: #e5e5e5;
     background-color: hsl(268, 100%, 82%);
     background: linear-gradient(
       to bottom,
       hsl(268, 100%, 82%),
       hsl(268, 100%, 72%),
       hsl(268, 100%, 62%));
     box-shadow: none;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
     background-color: hsl(268, 100%, 82%);
     background: linear-gradient(
       to bottom,
       hsl(268, 100%, 82%),
       hsl(268, 100%, 72%),
       hsl(268, 100%, 62%));
     box-shadow: none;
}

/* -------------- TOP-BAR -------------- */
#top-bar ul li ul {
    border: solid 1px hsl(335, 90%, 85%);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid hsl(335, 90%, 85%);
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: hsl(252, 67%, 52%);
}

/* -------------- BODY -------------- */
body {
     background: hsl(252, 83%, 70%);
     color: hsl();
}

#page-title, h1 {
    color: hsl(263, 38%, 47%)
}

#page-title {
    border-bottom-color: hsl(270, 100%, 83%);
}

/* -------------- SIDE-BAR -------------- */
#side-bar .side-block,
#interwiki .side-block {
    border: 1px solid hsl(335, 90%, 25%);
    box-shadow: 0 2px 6px rgba(121, 6, 54,.5);
    color: hsl(335, 90%, 25%);
    background: hsl(222, 100%, 95%);
}

#side-bar .heading,
#interwiki .heading {
    color: hsl(335, 90%, 25%);
    border-bottom: solid 1px hsl(252, 67%, 52%);
}

#side-bar .side-block.media {
    background-color: hsl(248, 66%, 88%);
}

#side-bar .side-block.resources {
    background-color: hsl(223, 100%, 95%);
}

#interwiki .side-block {
    background: hsl(335, 85%, 95%);
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px hsl(335, 90%, 25%);
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: hsl(335, 90%, 25%);
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: hsl(335, 95%, 45%);
}

#interwiki .menu-item img {
    filter: hue-rotate(340deg);    
}

/* -------------- FOOTER -------------- */
#footer {
    background: hsl(260, 100%, 75%);
    color: 	hsl(260, 57%, 56%);
}

div.page-rate-widget-box,
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #d4b7e2!important;
    border: solid #d4b7e2 1px!important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    border: solid #d4b7e2!important;
    border-width: 1px 0!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel a,
div.page-rate-widget-box span.cancel a:hover {
    color: #ffffff;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #EDD0FB!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    color: #ffffff!important;
}
div.page-rate-widget-box span.cancel a:hover,
div.page-rate-widget-box span.rateup a:hover,
div.page-rate-widget-box span.ratedown a:hover {
    background: #d4b7e2!important;
    color: #ffffff!important;
}

/* Info Module */
#page-content .rate-box-with-credit-button {
    background-color: hsl(280, 43%, 80%) !important;
    border: solid 1px #BEA1CC;

}
#page-content .creditButton p a {
    border-left-color: solid #BEA1CC;
}

#page-content .creditButton p a:hover {
    color: hsl(305, 100%, 15%);
}

/* -------------- LINKS -------------- */
a,
#side-bar a,
#interwiki a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#login-status ul a,
#login-status ul a:hover,
a.newpage {
    color: hsl(252, 67%, 52%);
}

a:visited {
    color: hsl(252, 67%, 52%);
}


/* -------------- OTHER ELEMENTS -------------- */
blockquote,
div.blockquote,
.code {
    border-color: hsl(231, 100%, 71%)!important;
    background-color: hsl(235, 100%, 97%)!important;
}

#page-content .wiki-content-table tr th {
     background-color: hsl(235, 100%, 76%)!important;
     color: hsl(215, 100%, 93%)!important;
     border: 1px solid hsl(335, 100%, 14%);
}

.scp-image-block .scp-image-caption {
    background-color: hsl(223, 100%, 72%)!important;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: hsl(234, 100%, 75%); /* color between tab list and content */
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-color: hsl(223, 100%, 72%)!important; /* tab background */
    border-color: transparent;
    color: hsl(335, 100%, 14%);
}

.yui-navset .yui-nav a em {
     background-color: hsl(223, 100%, 72%);
     box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
     border-color: hsl(335, 90%, 85%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background-image: none;
    background-color: hsl(222, 100%, 85%)!imporant; /* selected tab background */
    color: hsl(335, 100%, 98%);
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
    border-color: hsl(335, 55%, 55%);
    box-shadow: 0px 1px 2px rgba(0,0,0,0.7);
    background-color: hsl(222, 100%, 85%)!important;
}

.yui-navset .yui-content {
    background-color: hsl(235, 69%, 87%); /* content background color */
}

body {
    background: #e9e3fc;
}


[[/module]]


