		document.getElementById("glitch").innerHTML = "";
	}, 1550);
	setTimeout(() => {
		document.body.classList.add("eyes");
	}, 2200);
}

function clickDocument() {
	showMessage();

	if (showCount == document.querySelectorAll(".message").length) {
		errorEffect();
	}
}

