<script>
let showCount = 0;
let showGlitch, addGlitch;

function showMessage() {
	let messageElement = document.querySelector(".message:not(.show)");
	messageElement　?　messageElement.classList.add("show") : undefined;
	showCount++;
}

function generateGlitch() {
	const noise = document.createElement('div');
	noise.style.backgroundColor = `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.5)`;
	noise.style.height = `${Math.random() * 2.5}rem`;
	noise.style.left = `${Math.random() * 105 - 15}%`;
	noise.style.position = "absolute";
	noise.style.top = `${Math.random() * 102.5 - 5}%`;
	noise.style.width = `${Math.random() * 15}rem`;
	document.getElementById("glitch").appendChild(noise);
}

function errorEffect() {
	document.getElementById("noise").play();
	document.body.classList.add("nopointer");
	showGlitch = setInterval(generateGlitch, 10);
	setTimeout(() => {
		addGlitch = setInterval(function() {
			for (let i = 0; i <= 5; i++) {
				generateGlitch();
			}
		}, 5);
	}, 700);
	setTimeout(() => {
		clearInterval(showGlitch);
		clearInterval(addGlitch);
	}, 1050);
	setTimeout(() => {
		document.body.classList.add("dark");
		document.getElementById("glitch").innerHTML = "";
	}, 1550);
	setTimeout(() => {
		document.body.classList.add("eyes");
	}, 2200);
}

function clickDocument() {
	showMessage();

	if (showCount == document.querySelectorAll(".message").length) {
		errorEffect();
	}
}

document.addEventListener("click", clickDocument);
showMessage();
</script>
