.content-warning {
    font-size: 100%;
    font-weight: 900;
}

.earthworm--anth24 {
    --anth24-accent: rgb(10, 70, 10) !important;
}

div.earthworm--anth24 .earthworm__hub::after {
    background: #000;
    filter: unset !important;
}

div.earthworm--anth24 .earthworm__previous:has(a:hover) {
    background-image: linear-gradient(to right, #24f04d44, transparent);
}

div.earthworm--anth24 .earthworm__previous:has(a:hover) .earthworm__arrowhead, .earthworm--anth24 .earthworm__next:has(a:hover) .earthworm__arrowhead {
    background-image: linear-gradient(to left, #24f04d44, #24f04d44);
}

div.earthworm--anth24 .earthworm__next:has(a:hover) {
    background-image: linear-gradient(to left, #24f04d44, transparent);
}

.earthworm--anth24 a {
    text-decoration: none !important;
}

