[[module css]]
span.anime::after {
    content: "保持焦糖";
    -webkit-animation: my-anime 20s linear forwards infinite;
    animation: my-anime 20s linear forwards infinite;
}

span.cursor::before {
    content: "|";
    -webkit-animation: my-anime-2 0.97s linear forwards infinite;
    animation: my-anime-2 0.97s linear forwards infinite;
}

@keyframes my-anime {
15% {content: "保持焦糖";}
17.5% {content: "保持焦";}
20% {content: "保持";}
22.5% {content: "保";}
