97.5% {content: "保持";}
100% {content: "保持焦糖";}
}

@keyframes my-anime-2 {
0%, 50% {content: "|";}
50%, 100% {content: "";}
}

span.clock::after {
    font-weight: bold;
    content: "10";
    -webkit-animation: clock-anime 20s linear forwards infinite;
    animation: clock-anime 20s linear forwards infinite;
}

@keyframes clock-anime {
0%, 17.5%, 25%, 42.5%, 47.5%, 67.5%, 75%, 87.5%, 100% {content: " 10";}
5%, 30%, 52.5%, 80% {content: " 9";}
10%, 35%, 57.5%, 85% {content: " 8";}
15%, 40%, 62.5% {content: " 7";}
}

